// Initialize pagination - create multiple page requests
const postUrl = $json['LinkedIn Post URL'];

// Create array of page numbers (pages 1-10, max 1000 comments)
// Adjust maxPages based on your needs
const maxPages = 10;
const pageRequests = [];

for (let page = 1; page <= maxPages; page++) {
  pageRequests.push({
    json: {
      postUrl: postUrl,
      pageNumber: page,
      _originalFormData: { ...$json }  // Use spread operator to ensure it's an object
    }
  });
}

return pageRequests;

// Check if this page returned results
const hasResults = items.length > 0 && items[0].json;

if (!hasResults || items.length < 100) {
  // No results or less than 100 = last page, stop pagination
  // Return items to continue to Done branch
  return items;
}

// Has 100 results = might have more pages, continue loop
return items;

// Deduplicate commenters by profile URL
const seen = new Set();
const uniqueCommenters = [];

for (const item of items) {
  const profileUrl = item.json?.author?.profile_url;

  if (profileUrl && !seen.has(profileUrl)) {
    seen.add(profileUrl);
    uniqueCommenters.push({
      json: {
        name: item.json.author.name,
        profile_url: profileUrl
      }
    });
  }
}

return uniqueCommenters;

// This node runs when the loop is complete
// It receives ALL enriched profiles from the loop

// Get the stored form data from the first item
const firstItem = items[0]?.json;
if (!firstItem || !firstItem._formData) {
  throw new Error('Form data not found in items');
}

const selectedTitles = firstItem._formData.selectedTitles || [];
const selectedCountries = firstItem._formData.selectedCountries || [];

// Job title mapping with comprehensive synonyms
const titleMapping = {
  'CEO': ['CEO', 'Chief Executive Officer', 'Chief Exec'],
  'Founder': ['Founder', 'Co-Founder', 'Cofounder', 'Co Founder'],
  'Co-Founder': ['Co-Founder', 'Cofounder', 'Founder', 'Co Founder'],
  'CMO': ['CMO', 'Chief Marketing Officer'],
  'COO': ['COO', 'Chief Operating Officer', 'Chief Operations Officer'],
  'CTO': ['CTO', 'Chief Technology Officer', 'Chief Technical Officer'],
  'CFO': ['CFO', 'Chief Financial Officer'],
  'CRO': ['CRO', 'Chief Revenue Officer'],
  'CPO': ['CPO', 'Chief Product Officer'],
  'VP Marketing': ['VP Marketing', 'Vice President Marketing', 'VP of Marketing', 'Vice President of Marketing'],
  'VP Sales': ['VP Sales', 'Vice President Sales', 'VP of Sales', 'Vice President of Sales'],
  'VP Engineering': ['VP Engineering', 'Vice President Engineering', 'VP of Engineering', 'Vice President of Engineering'],
  'Head of Marketing': ['Head of Marketing', 'Marketing Head'],
  'Head of Sales': ['Head of Sales', 'Sales Head'],
  'Head of Growth': ['Head of Growth', 'Growth Head']
};

// Country mapping with localized names
const countryMapping = {
  'United States': ['United States', 'USA', 'US', 'America'],
  'United Kingdom': ['United Kingdom', 'UK', 'Britain', 'Great Britain', 'England'],
  'Germany': ['Germany', 'Deutschland'],
  'France': ['France', 'R√©publique fran√ßaise'],
  'Spain': ['Spain', 'Espa√±a'],
  'Netherlands': ['Netherlands', 'Nederland', 'Holland'],
  'Canada': ['Canada'],
  'Australia': ['Australia'],
  'Ireland': ['Ireland', '√âire'],
  'Singapore': ['Singapore']
};

// Build expanded title list
const expandedTitles = [];
for (const title of selectedTitles) {
  if (titleMapping[title]) {
    expandedTitles.push(...titleMapping[title]);
  }
}

// Build expanded country list
const expandedCountries = [];
for (const country of selectedCountries) {
  if (countryMapping[country]) {
    expandedCountries.push(...countryMapping[country]);
  }
}

// Process all enriched profiles
const matchedProfiles = [];

for (const item of items) {
  const profile = item.json;
  const basicInfo = profile.basic_info || {};
  const experience = profile.experience || [];

  // Extract job titles from current roles (up to 3)
  const jobTitles = [];
  for (let i = 0; i < Math.min(3, experience.length); i++) {
    const exp = experience[i];
    if (exp && exp.title) {
      jobTitles.push(exp.title);
    }
  }

  // Also check headline
  if (basicInfo.headline) {
    jobTitles.push(basicInfo.headline);
  }

  // Extract location/country - combine all sources
  const locationParts = [];

  // From basic_info.location
  if (basicInfo.location) {
    if (basicInfo.location.country) locationParts.push(basicInfo.location.country);
    if (basicInfo.location.full) locationParts.push(basicInfo.location.full);
    if (basicInfo.location.city) locationParts.push(basicInfo.location.city);
  }

  // From experience locations
  for (let i = 0; i < Math.min(3, experience.length); i++) {
    const exp = experience[i];
    if (exp && exp.location) {
      locationParts.push(exp.location);
    }
  }

  const location = locationParts.join(' ');

  // Normalize for case-insensitive matching
  const jobTitlesLower = jobTitles.map(t => String(t).toLowerCase());
  const locationLower = String(location).toLowerCase();

  // Check job title match
  let titleMatch = false;
  let matchedTitle = '';
  for (const title of expandedTitles) {
    const titleLower = title.toLowerCase();
    for (const jobTitle of jobTitlesLower) {
      if (jobTitle.includes(titleLower)) {
        titleMatch = true;
        matchedTitle = title;
        break;
      }
    }
    if (titleMatch) break;
  }

  // Check country match
  let countryMatch = false;
  let matchedCountry = '';
  for (const country of expandedCountries) {
    if (locationLower.includes(country.toLowerCase())) {
      countryMatch = true;
      matchedCountry = country;
      break;
    }
  }

  // Add to matched profiles if both match
  if (titleMatch && countryMatch) {
    matchedProfiles.push({
      json: {
        fullName: basicInfo.fullname || ((basicInfo.first_name || '') + ' ' + (basicInfo.last_name || '')).trim() || '',
        profileUrl: basicInfo.profile_url || '',
        jobTitle: jobTitles[0] || '',
        jobTitles: jobTitles,
        location: basicInfo.location?.full || basicInfo.location?.country || '',
        matchedJobTitle: matchedTitle,
        matchedCountry: matchedCountry,
        headline: basicInfo.headline || '',
        currentCompany: basicInfo.current_company || '',
        email: basicInfo.email || null
      }
    });
  }
}

return matchedProfiles;

// Store form selections in each item so they're accessible in the loop
const formTrigger = $('ICP Lead Extractor Form').first().json;

// Form data can be at different locations depending on form version
let formData;
if (formTrigger.data) {
  formData = formTrigger.data;
} else {
  formData = formTrigger;
}

const selectedTitles = formData['Target Job Titles'] || [];
const selectedCountries = formData['Target Countries'] || [];

// Add form selections to each profile item
const itemsWithFormData = [];
for (const item of items) {
  itemsWithFormData.push({
    json: {
      ...item.json,
      _formData: {
        selectedTitles: selectedTitles,
        selectedCountries: selectedCountries
      }
    }
  });
}

return itemsWithFormData;

// Preserve the _formData from the input through the enrichment
const enrichedProfile = $json;
const originalData = $('Loop Through Profiles').item.json;

// Combine enriched profile with preserved form data
return {
  json: {
    ...enrichedProfile,
    _formData: originalData._formData
  }
};

// Flatten the aggregated leads for CSV export
const leads = $json.leads || [];

if (leads.length === 0) {
  return [{
    json: {
      message: 'No matching profiles found'
    }
  }];
}

// Flatten each lead into CSV-ready format
const flattenedLeads = leads.map(lead => ({
  json: {
    'Full Name': lead.fullName || '',
    'LinkedIn Profile URL': lead.profileUrl || '',
    'Job Title': lead.jobTitle || '',
    'All Job Titles': Array.isArray(lead.jobTitles) ? lead.jobTitles.join(', ') : (lead.jobTitles || ''),
    'Location': lead.location || '',
    'Matched Job Title': lead.matchedJobTitle || '',
    'Matched Country': lead.matchedCountry || '',
    'Headline': lead.headline || '',
    'Current Company': lead.currentCompany || '',
    'Email': lead.email || ''
  }
}));

return flattenedLeads;
