// Get the data from the HTTP Request node
const inputData = $input.all();

// The data is directly an array with one object that has a 'data' property
const rawData = inputData[0].json.data;

// Transform each bucket (day) into a flat structure
const outputData = [];

for (const bucket of rawData) {
  const date = bucket.start_time_iso.split('T')[0]; // Get just YYYY-MM-DD

  // If there are results for this day
  if (bucket.results && bucket.results.length > 0) {
    for (const result of bucket.results) {
      outputData.push({
        date: date,
        model: result.model || 'N/A',
        input_tokens: result.input_tokens || 0,
        output_tokens: result.output_tokens || 0
      });
    }
  } else {
    // If no usage for this day, add a row showing zero usage
    outputData.push({
      date: date,
      model: 'No usage',
      input_tokens: 0,
      output_tokens: 0
    });
  }
}

return outputData.map(item => ({ json: item }));
