// Validate & Normalize Data
// This code validates incoming data schema, normalizes field names,
// converts timestamps to ISO format, validates flight numbers,
// checks for required fields, sanitizes input, and adds validation timestamp

const item = $input.item.json;

// Helper function to validate IATA flight number format (e.g., AA1234, BA456)
function validateFlightNumber(flightNumber) {
  if (!flightNumber) return false;
  // IATA format: 2-3 letter airline code + 1-4 digit flight number
  const iataPattern = /^[A-Z]{2,3}\d{1,4}$/i;
  return iataPattern.test(flightNumber.toString().replace(/\s+/g, ''));
}

// Helper function to convert various timestamp formats to ISO format
function normalizeTimestamp(timestamp) {
  if (!timestamp) return null;
  try {
    const date = new Date(timestamp);
    if (isNaN(date.getTime())) return null;
    return date.toISOString();
  } catch (error) {
    return null;
  }
}

// Helper function to sanitize string input
function sanitizeString(str) {
  if (typeof str !== 'string') return str;
  return str.trim().replace(/[<>"']/g, '');
}

// Normalize field names (handle various naming conventions)
const normalizedData = {
  flightNumber: item.flightNumber || item.flight_number || item.FlightNumber || item.flight || null,
  departureTime: item.departureTime || item.departure_time || item.DepartureTime || item.scheduled_departure || null,
  arrivalTime: item.arrivalTime || item.arrival_time || item.ArrivalTime || item.scheduled_arrival || null,
  status: item.status || item.Status || item.flight_status || null,
  origin: item.origin || item.departure_airport || item.from || null,
  destination: item.destination || item.arrival_airport || item.to || null,
  aircraft: item.aircraft || item.aircraft_type || null,
  gate: item.gate || item.departure_gate || null,
  terminal: item.terminal || item.departure_terminal || null,
  weather: item.weather || item.weather_data || null,
  delay: item.delay || item.delay_minutes || null,
  rawData: item
};

// Validation results
const validationErrors = [];
const validationWarnings = [];

// Check required fields
const requiredFields = ['flightNumber', 'departureTime', 'arrivalTime', 'status'];
for (const field of requiredFields) {
  if (!normalizedData[field]) {
    validationErrors.push(`Missing required field: ${field}`);
  }
}

// Validate flight number format
if (normalizedData.flightNumber) {
  normalizedData.flightNumber = sanitizeString(normalizedData.flightNumber.toString());
  if (!validateFlightNumber(normalizedData.flightNumber)) {
    validationErrors.push(`Invalid flight number format: ${normalizedData.flightNumber}`);
  }
} else {
  validationErrors.push('Flight number is missing');
}

// Normalize timestamps to ISO format
if (normalizedData.departureTime) {
  const isoTime = normalizeTimestamp(normalizedData.departureTime);
  if (isoTime) {
    normalizedData.departureTime = isoTime;
  } else {
    validationErrors.push('Invalid departure time format');
  }
}

if (normalizedData.arrivalTime) {
  const isoTime = normalizeTimestamp(normalizedData.arrivalTime);
  if (isoTime) {
    normalizedData.arrivalTime = isoTime;
  } else {
    validationErrors.push('Invalid arrival time format');
  }
}

// Sanitize string fields
if (normalizedData.status) {
  normalizedData.status = sanitizeString(normalizedData.status.toString()).toUpperCase();
}
if (normalizedData.origin) {
  normalizedData.origin = sanitizeString(normalizedData.origin.toString()).toUpperCase();
}
if (normalizedData.destination) {
  normalizedData.destination = sanitizeString(normalizedData.destination.toString()).toUpperCase();
}

// Add validation metadata
const validationTimestamp = new Date().toISOString();
const isValid = validationErrors.length === 0;

// Return normalized data with validation status
return {
  json: {
    ...normalizedData,
    validation: {
      isValid: isValid,
      timestamp: validationTimestamp,
      errors: validationErrors,
      warnings: validationWarnings
    }
  }
};

// Idempotency & Deduplication Logic
// Generate unique event ID using SHA-256 hash and check for duplicates

const crypto = require('crypto');

// Extract event data from input
const item = $input.item.json;
const flightNumber = item.flightNumber || item.flight_number || '';
const departureTime = item.departureTime || item.departure_time || item.scheduledDeparture || '';
const eventType = item.eventType || item.event_type || item.type || '';

// Generate unique event ID using SHA-256 hash
const eventString = `${flightNumber}|${departureTime}|${eventType}`;
const eventId = crypto.createHash('sha256').update(eventString).digest('hex');

// Get current timestamp
const now = new Date();
const twentyFourHoursAgo = new Date(now.getTime() - 24 * 60 * 60 * 1000);

// Initialize in-memory cache if not exists (using workflow static data)
const cache = $workflow.staticData.eventCache || {};
const cacheTimestamps = $workflow.staticData.eventCacheTimestamps || {};

// Clean up old entries (sliding window - remove events older than 24 hours)
for (const [key, timestamp] of Object.entries(cacheTimestamps)) {
  if (new Date(timestamp) < twentyFourHoursAgo) {
    delete cache[key];
    delete cacheTimestamps[key];
  }
}

// Check if event is duplicate
const isDuplicate = cache.hasOwnProperty(eventId);

// If not duplicate, add to cache
if (!isDuplicate) {
  cache[eventId] = true;
  cacheTimestamps[eventId] = now.toISOString();
}

// Save updated cache back to workflow static data
$workflow.staticData.eventCache = cache;
$workflow.staticData.eventCacheTimestamps = cacheTimestamps;

// Return enriched item with idempotency data
return {
  json: {
    ...item,
    idempotencyKey: eventId,
    isDuplicate: isDuplicate,
    eventStatus: isDuplicate ? 'duplicate' : 'new',
    deduplicationTimestamp: now.toISOString(),
    eventHash: eventId,
    cacheSize: Object.keys(cache).length
  }
};

// Apply Operational Rules Engine
// Process each disruption event and apply airline operational rules

const item = $input.item.json;

// Initialize rule violations array
const ruleViolations = [];
let operationalFeasibilityScore = 100;

// Extract relevant data from the AI analysis
const disruptionType = item.disruptionType || item.type || 'unknown';
const severity = item.severity || 'medium';
const affectedFlights = item.affectedFlights || [];
const proposedActions = item.proposedActions || [];

// Rule 1: Crew Duty Time Limits (max 14 hours)
const crewDutyTime = item.crewDutyTime || item.estimatedCrewDutyTime || 0;
if (crewDutyTime > 14) {
  ruleViolations.push({
    rule: 'CREW_DUTY_TIME_EXCEEDED',
    description: `Crew duty time of ${crewDutyTime} hours exceeds maximum of 14 hours`,
    severity: 'critical',
    impact: 'Flight cannot proceed without crew change'
  });
  operationalFeasibilityScore -= 30;
}

// Rule 2: Aircraft Turnaround Time (min 45 minutes)
const turnaroundTime = item.turnaroundTime || item.estimatedTurnaroundTime || 60;
if (turnaroundTime < 45) {
  ruleViolations.push({
    rule: 'INSUFFICIENT_TURNAROUND_TIME',
    description: `Turnaround time of ${turnaroundTime} minutes is below minimum of 45 minutes`,
    severity: 'high',
    impact: 'Risk of delayed departure and cascading delays'
  });
  operationalFeasibilityScore -= 20;
}

// Rule 3: Slot Constraints at Congested Airports
const airport = item.airport || item.departureAirport || '';
const congestedAirports = ['LHR', 'JFK', 'LAX', 'ORD', 'ATL', 'DXB', 'CDG', 'FRA', 'AMS', 'HKG'];
const hasSlotConstraint = congestedAirports.includes(airport);

if (hasSlotConstraint) {
  const slotAvailable = item.slotAvailable !== false; // Default to true if not specified
  if (!slotAvailable) {
    ruleViolations.push({
      rule: 'SLOT_CONSTRAINT_VIOLATION',
      description: `No available slot at congested airport ${airport}`,
      severity: 'critical',
      impact: 'Cannot depart without allocated slot'
    });
    operationalFeasibilityScore -= 40;
  }
}

// Rule 4: Maintenance Requirements
const maintenanceStatus = item.maintenanceStatus || 'ok';
const hoursSinceLastMaintenance = item.hoursSinceLastMaintenance || 0;
const maintenanceRequired = maintenanceStatus === 'required' || hoursSinceLastMaintenance > 100;

if (maintenanceRequired) {
  ruleViolations.push({
    rule: 'MAINTENANCE_REQUIRED',
    description: `Aircraft requires maintenance (status: ${maintenanceStatus}, hours since last: ${hoursSinceLastMaintenance})`,
    severity: 'critical',
    impact: 'Aircraft cannot operate until maintenance completed'
  });
  operationalFeasibilityScore -= 50;
}

// Rule 5: Passenger Connection Times (min 60 minutes international)
const connectingPassengers = item.connectingPassengers || [];
connectingPassengers.forEach((pax, index) => {
  const connectionTime = pax.connectionTime || 0;
  const isInternational = pax.isInternational || false;
  const minConnectionTime = isInternational ? 60 : 45;

  if (connectionTime < minConnectionTime) {
    ruleViolations.push({
      rule: 'INSUFFICIENT_CONNECTION_TIME',
      description: `Passenger connection time of ${connectionTime} minutes is below minimum of ${minConnectionTime} minutes (${isInternational ? 'international' : 'domestic'})`,
      severity: 'high',
      impact: 'Passengers will miss connections, rebooking required'
    });
    operationalFeasibilityScore -= 10;
  }
});

// Rule 6: IATA Regulations Compliance
const iataCompliance = {
  delayCompensation: false,
  passengerRights: false,
  baggageHandling: true
};

// Check if delay exceeds thresholds for compensation (EU261 style)
const delayMinutes = item.delayMinutes || item.estimatedDelay || 0;
if (delayMinutes > 180) {
  iataCompliance.delayCompensation = true;
  ruleViolations.push({
    rule: 'IATA_DELAY_COMPENSATION_REQUIRED',
    description: `Delay of ${delayMinutes} minutes triggers passenger compensation requirements`,
    severity: 'medium',
    impact: 'Compensation and care obligations apply'
  });
}

if (delayMinutes > 120) {
  iataCompliance.passengerRights = true;
  ruleViolations.push({
    rule: 'IATA_PASSENGER_CARE_REQUIRED',
    description: `Delay of ${delayMinutes} minutes triggers passenger care requirements (meals, accommodation)`,
    severity: 'medium',
    impact: 'Must provide passenger care and assistance'
  });
}

// Calculate final operational feasibility score (0-100)
operationalFeasibilityScore = Math.max(0, Math.min(100, operationalFeasibilityScore));

// Determine operational status
let operationalStatus = 'FEASIBLE';
if (operationalFeasibilityScore < 30) {
  operationalStatus = 'NOT_FEASIBLE';
} else if (operationalFeasibilityScore < 60) {
  operationalStatus = 'REQUIRES_INTERVENTION';
} else if (operationalFeasibilityScore < 80) {
  operationalStatus = 'FEASIBLE_WITH_CONSTRAINTS';
}

// Enrich data with operational context
const enrichedData = {
  ...item,
  operationalRules: {
    ruleViolations: ruleViolations,
    violationCount: ruleViolations.length,
    criticalViolations: ruleViolations.filter(v => v.severity === 'critical').length,
    highViolations: ruleViolations.filter(v => v.severity === 'high').length,
    mediumViolations: ruleViolations.filter(v => v.severity === 'medium').length
  },
  operationalFeasibility: {
    score: operationalFeasibilityScore,
    status: operationalStatus,
    canProceed: operationalFeasibilityScore >= 60,
    requiresApproval: operationalFeasibilityScore < 80 && operationalFeasibilityScore >= 60
  },
  iataCompliance: iataCompliance,
  operationalContext: {
    congestedAirport: hasSlotConstraint,
    maintenanceRequired: maintenanceRequired,
    crewConstraints: crewDutyTime > 12,
    connectionImpact: connectingPassengers.length,
    compensationLiability: iataCompliance.delayCompensation,
    processedAt: new Date().toISOString(),
    rulesEngineVersion: '1.0'
  }
};

return enrichedData;

// Trigger Rebooking Workflow
// This code initiates the rebooking process by calling internal API endpoint

const items = $input.all();
const rebookingRequests = [];

for (const item of items) {
  const passengerList = item.json.passengers || [];
  const flightDetails = item.json.flightDetails || {};
  const alternativeFlights = item.json.alternativeFlights || [];
  const priorityLevel = item.json.priorityLevel || 'medium';

  // Generate unique rebooking request ID
  const rebookingRequestId = `RBK-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

  // Create rebooking request record
  const rebookingRequest = {
    requestId: rebookingRequestId,
    timestamp: new Date().toISOString(),
    originalFlight: {
      flightNumber: flightDetails.flightNumber,
      departure: flightDetails.departure,
      arrival: flightDetails.arrival,
      scheduledDeparture: flightDetails.scheduledDeparture
    },
    passengers: passengerList.map(p => ({
      passengerId: p.id,
      name: p.name,
      pnr: p.pnr,
      seatClass: p.seatClass,
      specialRequirements: p.specialRequirements || []
    })),
    alternativeOptions: alternativeFlights.map(f => ({
      flightNumber: f.flightNumber,
      departure: f.departure,
      arrival: f.arrival,
      availableSeats: f.availableSeats,
      seatClass: f.seatClass
    })),
    priority: priorityLevel,
    status: 'initiated',
    initiatedBy: 'disruption-management-system',
    initiatedAt: new Date().toISOString()
  };

  // Log rebooking initiation
  console.log(`Rebooking workflow initiated for request ID: ${rebookingRequestId}`);
  console.log(`Priority Level: ${priorityLevel}`);
  console.log(`Passengers affected: ${passengerList.length}`);
  console.log(`Alternative flights available: ${alternativeFlights.length}`);

  // Prepare API call payload (simulated - replace with actual API endpoint)
  const apiPayload = {
    endpoint: '/api/rebooking/initiate',
    method: 'POST',
    data: rebookingRequest
  };

  // Add to results
  rebookingRequests.push({
    json: {
      success: true,
      rebookingRequestId: rebookingRequestId,
      status: 'initiated',
      passengersCount: passengerList.length,
      alternativeFlightsCount: alternativeFlights.length,
      priorityLevel: priorityLevel,
      apiPayload: apiPayload,
      rebookingRequest: rebookingRequest,
      message: `Rebooking workflow successfully initiated with request ID: ${rebookingRequestId}`
    }
  });
}

return rebookingRequests;
