// Aggregate multi-channel revenue data from different sources
// Calculate total revenue per channel and overall total

const items = $input.all();

// Initialize revenue tracking object
const revenueByChannel = {};
let overallTotal = 0;

// Process each item and aggregate by channel
for (const item of items) {
  const channel = item.json.channel || 'Unknown';
  const revenue = parseFloat(item.json.revenue) || 0;

  // Add to channel total
  if (!revenueByChannel[channel]) {
    revenueByChannel[channel] = 0;
  }
  revenueByChannel[channel] += revenue;

  // Add to overall total
  overallTotal += revenue;
}

// Format the aggregated data
const channelBreakdown = Object.entries(revenueByChannel).map(([channel, total]) => ({
  channel,
  revenue: total,
  percentage: ((total / overallTotal) * 100).toFixed(2)
}));

// Return aggregated results
return [
  {
    json: {
      overallTotal,
      channelBreakdown,
      totalChannels: Object.keys(revenueByChannel).length,
      reportDate: new Date().toISOString(),
      period: 'monthly'
    }
  }
];

// Calculate Tax Liabilities
// This code calculates various tax liabilities based on aggregated revenue

// Get aggregated revenue data from previous node
const revenueData = $input.all();

// Get tax rates from Workflow Configuration node
const config = $('Workflow Configuration').first().json;

// Initialize tax calculation results
const taxResults = [];

// Process each revenue item
for (const item of revenueData) {
  const revenue = item.json;

  // Extract revenue amounts
  const totalRevenue = revenue.totalRevenue || 0;
  const domesticRevenue = revenue.domesticRevenue || 0;
  const internationalRevenue = revenue.internationalRevenue || 0;

  // Get tax rates from configuration (with defaults if not set)
  const incomeTaxRate = config.incomeTaxRate || 0.21;
  const vatRate = config.vatRate || 0.20;
  const gstRate = config.gstRate || 0.10;
  const withholdingTaxRate = config.withholdingTaxRate || 0.15;

  // Calculate different tax liabilities
  const incomeTax = totalRevenue * incomeTaxRate;
  const vat = domesticRevenue * vatRate;
  const gst = internationalRevenue * gstRate;
  const withholdingTax = internationalRevenue * withholdingTaxRate;

  // Calculate total tax liability
  const totalTaxLiability = incomeTax + vat + gst + withholdingTax;

  // Calculate net revenue after tax
  const netRevenue = totalRevenue - totalTaxLiability;

  // Create result object
  taxResults.push({
    json: {
      ...revenue,
      taxCalculations: {
        incomeTax: parseFloat(incomeTax.toFixed(2)),
        vat: parseFloat(vat.toFixed(2)),
        gst: parseFloat(gst.toFixed(2)),
        withholdingTax: parseFloat(withholdingTax.toFixed(2)),
        totalTaxLiability: parseFloat(totalTaxLiability.toFixed(2)),
        netRevenue: parseFloat(netRevenue.toFixed(2))
      },
      taxRatesApplied: {
        incomeTaxRate,
        vatRate,
        gstRate,
        withholdingTaxRate
      },
      calculationDate: new Date().toISOString()
    }
  });
}

return taxResults;

// Generate rolling forecast for the next 6 months based on historical revenue trends and calculated tax liabilities

const items = $input.all();

// Extract historical data from previous nodes
const historicalData = items.map(item => item.json);

// Calculate average monthly revenue growth rate
let totalRevenue = 0;
let revenueCount = 0;

historicalData.forEach(data => {
  if (data.revenue) {
    totalRevenue += data.revenue;
    revenueCount++;
  }
});

const avgMonthlyRevenue = revenueCount > 0 ? totalRevenue / revenueCount : 0;

// Calculate growth rate (simplified - assumes 5% monthly growth)
const growthRate = 0.05;

// Get current tax liability rate
const taxRate = historicalData[0]?.taxRate || 0.25;

// Generate 6-month rolling forecast
const forecast = [];
const currentDate = new Date();

for (let i = 1; i <= 6; i++) {
  const forecastDate = new Date(currentDate);
  forecastDate.setMonth(currentDate.getMonth() + i);

  const projectedRevenue = avgMonthlyRevenue * Math.pow(1 + growthRate, i);
  const projectedTaxLiability = projectedRevenue * taxRate;
  const netRevenue = projectedRevenue - projectedTaxLiability;

  forecast.push({
    month: forecastDate.toISOString().slice(0, 7),
    monthName: forecastDate.toLocaleString('default', { month: 'long', year: 'numeric' }),
    projectedRevenue: Math.round(projectedRevenue * 100) / 100,
    projectedTaxLiability: Math.round(projectedTaxLiability * 100) / 100,
    netRevenue: Math.round(netRevenue * 100) / 100,
    growthRate: growthRate * 100 + '%',
    taxRate: taxRate * 100 + '%'
  });
}

// Return forecast data
return forecast.map(item => ({ json: item }));

// Calculate Tax Deductions for Small Businesses
// Includes standard deduction, business expenses, and depreciation allowances

const items = $input.all();
const results = [];

for (const item of items) {
  const revenue = item.json.revenue || item.json.totalRevenue || 0;
  const expenses = item.json.expenses || 0;

  // Standard deduction for small businesses (typically a fixed amount or percentage)
  const standardDeduction = Math.min(12950, revenue * 0.05); // $12,950 or 5% of revenue, whichever is lower

  // Business expenses deduction (operating costs, supplies, utilities, etc.)
  const businessExpensesDeduction = expenses * 0.85; // 85% of reported expenses are deductible

  // Depreciation allowances (for equipment, vehicles, property)
  const depreciableAssets = item.json.assets || 0;
  const depreciationRate = 0.20; // 20% annual depreciation using MACRS
  const depreciationAllowance = depreciableAssets * depreciationRate;

  // Section 179 deduction (immediate expensing for qualifying equipment)
  const section179Limit = 1080000; // 2023 limit
  const qualifyingEquipment = item.json.qualifyingEquipment || 0;
  const section179Deduction = Math.min(section179Limit, qualifyingEquipment);

  // Home office deduction (if applicable)
  const homeOfficeSquareFeet = item.json.homeOfficeSquareFeet || 0;
  const homeOfficeDeduction = Math.min(homeOfficeSquareFeet * 5, 1500); // $5 per sq ft, max $1,500

  // Calculate total deductions
  const totalDeductions = 
    standardDeduction + 
    businessExpensesDeduction + 
    depreciationAllowance + 
    section179Deduction + 
    homeOfficeDeduction;

  // Calculate taxable income after deductions
  const taxableIncome = Math.max(0, revenue - totalDeductions);

  // Calculate tax savings (assuming 25% tax rate)
  const taxRate = 0.25;
  const taxSavings = totalDeductions * taxRate;

  results.push({
    json: {
      ...item.json,
      deductions: {
        standardDeduction: Math.round(standardDeduction * 100) / 100,
        businessExpensesDeduction: Math.round(businessExpensesDeduction * 100) / 100,
        depreciationAllowance: Math.round(depreciationAllowance * 100) / 100,
        section179Deduction: Math.round(section179Deduction * 100) / 100,
        homeOfficeDeduction: Math.round(homeOfficeDeduction * 100) / 100,
        totalDeductions: Math.round(totalDeductions * 100) / 100
      },
      taxableIncome: Math.round(taxableIncome * 100) / 100,
      taxSavings: Math.round(taxSavings * 100) / 100,
      effectiveTaxRate: revenue > 0 ? Math.round((taxableIncome / revenue) * 10000) / 100 : 0,
      calculationDate: new Date().toISOString()
    }
  });
}

return results;

// Apply Progressive Tax Brackets
// Calculate tax liability using tiered rates for medium and large businesses
// Tax brackets: 10%, 20%, 30%, 37% based on income thresholds

const items = $input.all();
const results = [];

// Define progressive tax brackets
const taxBrackets = [
  { threshold: 0, rate: 0.10, name: 'Tier 1 (0-50k)' },
  { threshold: 50000, rate: 0.20, name: 'Tier 2 (50k-100k)' },
  { threshold: 100000, rate: 0.30, name: 'Tier 3 (100k-250k)' },
  { threshold: 250000, rate: 0.37, name: 'Tier 4 (250k+)' }
];

// Process each item
for (const item of items) {
  const income = item.json.totalRevenue || item.json.income || 0;

  let totalTax = 0;
  let remainingIncome = income;
  const bracketBreakdown = [];

  // Calculate tax for each bracket
  for (let i = 0; i < taxBrackets.length; i++) {
    const currentBracket = taxBrackets[i];
    const nextBracket = taxBrackets[i + 1];

    if (remainingIncome <= 0) break;

    let taxableInThisBracket = 0;

    if (nextBracket) {
      // Not the highest bracket
      const bracketCap = nextBracket.threshold - currentBracket.threshold;
      taxableInThisBracket = Math.min(remainingIncome, bracketCap);
    } else {
      // Highest bracket - tax all remaining income
      taxableInThisBracket = remainingIncome;
    }

    const taxForBracket = taxableInThisBracket * currentBracket.rate;
    totalTax += taxForBracket;
    remainingIncome -= taxableInThisBracket;

    if (taxableInThisBracket > 0) {
      bracketBreakdown.push({
        bracket: currentBracket.name,
        rate: currentBracket.rate * 100 + '%',
        taxableAmount: Math.round(taxableInThisBracket * 100) / 100,
        taxAmount: Math.round(taxForBracket * 100) / 100
      });
    }
  }

  // Calculate effective tax rate
  const effectiveTaxRate = income > 0 ? (totalTax / income) * 100 : 0;

  results.push({
    json: {
      ...item.json,
      progressiveTaxCalculation: {
        totalIncome: Math.round(income * 100) / 100,
        totalTax: Math.round(totalTax * 100) / 100,
        effectiveTaxRate: Math.round(effectiveTaxRate * 100) / 100 + '%',
        netIncome: Math.round((income - totalTax) * 100) / 100,
        bracketBreakdown: bracketBreakdown,
        calculationDate: new Date().toISOString()
      }
    }
  });
}

return results;

// Detect revenue anomalies using statistical analysis (z-score method)
// Flag outliers that exceed 2 standard deviations from the mean

const items = $input.all();

// Extract revenue values from all items
const revenues = items.map(item => {
  const revenue = item.json.revenue || item.json.totalRevenue || item.json.projectedRevenue || 0;
  return parseFloat(revenue);
}).filter(rev => !isNaN(rev) && rev > 0);

// Check if we have enough data points
if (revenues.length < 3) {
  return items.map(item => ({
    json: {
      ...item.json,
      anomalyDetection: {
        isAnomaly: false,
        reason: 'Insufficient data points for statistical analysis',
        dataPoints: revenues.length
      }
    }
  }));
}

// Calculate mean (average)
const mean = revenues.reduce((sum, val) => sum + val, 0) / revenues.length;

// Calculate standard deviation
const squaredDiffs = revenues.map(val => Math.pow(val - mean, 2));
const variance = squaredDiffs.reduce((sum, val) => sum + val, 0) / revenues.length;
const stdDev = Math.sqrt(variance);

// Z-score threshold (2 standard deviations)
const zScoreThreshold = 2;

// Process each item and detect anomalies
const results = items.map(item => {
  const revenue = item.json.revenue || item.json.totalRevenue || item.json.projectedRevenue || 0;
  const revenueValue = parseFloat(revenue);

  // Calculate z-score for this revenue value
  const zScore = stdDev !== 0 ? Math.abs((revenueValue - mean) / stdDev) : 0;

  // Determine if this is an anomaly
  const isAnomaly = zScore > zScoreThreshold;

  // Determine anomaly type
  let anomalyType = 'normal';
  if (isAnomaly) {
    anomalyType = revenueValue > mean ? 'unusually_high' : 'unusually_low';
  }

  return {
    json: {
      ...item.json,
      anomalyDetection: {
        isAnomaly,
        zScore: parseFloat(zScore.toFixed(2)),
        threshold: zScoreThreshold,
        anomalyType,
        revenue: revenueValue,
        mean: parseFloat(mean.toFixed(2)),
        stdDev: parseFloat(stdDev.toFixed(2)),
        deviationFromMean: parseFloat((revenueValue - mean).toFixed(2)),
        percentageDeviation: parseFloat(((revenueValue - mean) / mean * 100).toFixed(2)),
        detectionDate: new Date().toISOString()
      }
    }
  };
});

// Add summary statistics
const anomalyCount = results.filter(r => r.json.anomalyDetection.isAnomaly).length;
const anomalyRate = (anomalyCount / results.length * 100).toFixed(2);

// Add summary to first item
if (results.length > 0) {
  results[0].json.anomalyDetection.summary = {
    totalItems: results.length,
    anomaliesDetected: anomalyCount,
    anomalyRate: `${anomalyRate}%`,
    mean: parseFloat(mean.toFixed(2)),
    stdDev: parseFloat(stdDev.toFixed(2)),
    minRevenue: Math.min(...revenues),
    maxRevenue: Math.max(...revenues)
  };
}

return results;

// Generate Scenario Analysis for Tax Forecasting
// Creates best-case, worst-case, and most-likely scenarios with different growth rates and market conditions

const items = $input.all();

// Get forecast data from previous node
const forecastData = items.map(item => item.json);

// Define scenario parameters
const scenarios = {
  bestCase: {
    name: 'Best Case',
    growthRate: 0.15,  // 15% monthly growth
    marketCondition: 'Strong market expansion',
    taxEfficiency: 0.95,  // 5% tax optimization
    description: 'Optimistic scenario with strong market growth and effective tax planning'
  },
  mostLikely: {
    name: 'Most Likely',
    growthRate: 0.05,  // 5% monthly growth
    marketCondition: 'Stable market conditions',
    taxEfficiency: 1.0,  // No tax optimization
    description: 'Realistic scenario based on historical trends'
  },
  worstCase: {
    name: 'Worst Case',
    growthRate: -0.05,  // -5% monthly decline
    marketCondition: 'Economic downturn',
    taxEfficiency: 1.05,  // 5% additional tax burden
    description: 'Conservative scenario with market contraction'
  }
};

// Get base revenue from forecast data
const baseRevenue = forecastData[0]?.projectedRevenue || 100000;
const baseTaxRate = forecastData[0]?.taxRate ? parseFloat(forecastData[0].taxRate) / 100 : 0.25;

// Generate scenarios for next 6 months
const scenarioResults = [];

for (const [scenarioKey, scenario] of Object.entries(scenarios)) {
  const monthlyProjections = [];
  const currentDate = new Date();

  for (let month = 1; month <= 6; month++) {
    const forecastDate = new Date(currentDate);
    forecastDate.setMonth(currentDate.getMonth() + month);

    // Calculate projected revenue with growth rate
    const projectedRevenue = baseRevenue * Math.pow(1 + scenario.growthRate, month);

    // Calculate tax liability with efficiency factor
    const taxLiability = projectedRevenue * baseTaxRate * scenario.taxEfficiency;

    // Calculate net revenue
    const netRevenue = projectedRevenue - taxLiability;

    monthlyProjections.push({
      month: forecastDate.toISOString().slice(0, 7),
      monthName: forecastDate.toLocaleString('default', { month: 'long', year: 'numeric' }),
      projectedRevenue: Math.round(projectedRevenue * 100) / 100,
      taxLiability: Math.round(taxLiability * 100) / 100,
      netRevenue: Math.round(netRevenue * 100) / 100
    });
  }

  // Calculate totals for the scenario
  const totalRevenue = monthlyProjections.reduce((sum, m) => sum + m.projectedRevenue, 0);
  const totalTax = monthlyProjections.reduce((sum, m) => sum + m.taxLiability, 0);
  const totalNet = monthlyProjections.reduce((sum, m) => sum + m.netRevenue, 0);

  scenarioResults.push({
    json: {
      scenario: scenario.name,
      scenarioKey: scenarioKey,
      description: scenario.description,
      parameters: {
        growthRate: (scenario.growthRate * 100).toFixed(2) + '%',
        marketCondition: scenario.marketCondition,
        taxEfficiency: scenario.taxEfficiency,
        baseTaxRate: (baseTaxRate * 100).toFixed(2) + '%'
      },
      monthlyProjections: monthlyProjections,
      sixMonthTotals: {
        totalRevenue: Math.round(totalRevenue * 100) / 100,
        totalTaxLiability: Math.round(totalTax * 100) / 100,
        totalNetRevenue: Math.round(totalNet * 100) / 100,
        averageMonthlyRevenue: Math.round((totalRevenue / 6) * 100) / 100
      },
      generatedAt: new Date().toISOString()
    }
  });
}

return scenarioResults;
