return items.map(item => {
  const data = item.json;
  const fullName = `${data.firstName || ''} ${data.lastName || ''}`.trim();
  let combinedText = ``;

  // Process educations (handle array or single object)
  if (data.educations) {
    if (Array.isArray(data.educations)) {
      data.educations.forEach(edu => {
        combinedText += `Education at ${edu.schoolName} in ${edu.fieldOfStudy} from ${edu.start.year} to ${edu.end.year}.\n`;
      });
    } else {
      combinedText += `Education at ${data.educations.schoolName} in ${data.educations.fieldOfStudy} from ${data.educations.start.year} to ${data.educations.end.year}.\n`;
    }
  }

  // Process fullPositions (handle array or single object)
  if (data.fullPositions) {
    if (Array.isArray(data.fullPositions)) {
      data.fullPositions.forEach(pos => {
        combinedText += `Worked as ${pos.title} at ${pos.companyName} starting ${pos.start.year}.\n`;
      });
    } else {
      combinedText += `Worked as ${data.fullPositions.title} at ${data.fullPositions.companyName} starting ${data.fullPositions.start.year}.\n`;
    }
  }

  // Optionally, include headline if available
  if (data.headline) {
    combinedText = `Headline: ${data.headline}\n` + combinedText;
  }

  return { json: { fullName, combinedText } };
});
