const expected_code = $('When fetching a dataset row').item.json.expected_output
const actual_code = $json.content

function levenshteinDistance(str1, str2) {
  const m = str1.length;
  const n = str2.length;
  const dp = Array(m + 1).fill().map(() => Array(n + 1).fill(0));

  for (let i = 0; i <= m; i++) {
    dp[i][0] = i;
  }

  for (let j = 0; j <= n; j++) {
    dp[0][j] = j;
  }

  for (let i = 1; i <= m; i++) {
    for (let j = 1; j <= n; j++) {
      if (str1[i - 1] === str2[j - 1]) {
        dp[i][j] = dp[i - 1][j - 1];
      } else {
        dp[i][j] = 1 + Math.min(
          dp[i - 1][j],     // deletion
          dp[i][j - 1],     // insertion
          dp[i - 1][j - 1]  // substitution
        );
      }
    }
  }

  return dp[m][n];
}

const dist = levenshteinDistance(
  expected_code, 
  actual_code
)

const max_dist = Math.max(
  expected_code.length,
  actual_code.length
)

console.log('truth', expected_code)
console.log('effort', actual_code)
console.log('dist', dist)
console.log('max_dist', max_dist)

$input.item.json.score = 1 - (dist / max_dist)

return $input.item;
