const value = $json.items;
const isArray = Array.isArray(value);

return [
  {
    json: {
      isArray: isArray,
      original: value
    }
  }
];

const input = items[0].json.original;
const posts = Array.isArray(input) ? input : [];

// –§–∏–ª—å—Ç—Ä—É–µ–º —Ç–µ, –≥–¥–µ item_price –ø—Ä–∏—Å—É—Ç—Å—Ç–≤—É–µ—Ç
const filteredPosts = posts.filter(post =>
  post && post.item_price !== null && post.item_price !== undefined
);

// –û—Å—Ç–∞–≤–ª—è–µ–º —Ç–æ–ª—å–∫–æ –Ω—É–∂–Ω—ã–µ –ø–æ–ª—è
const extracted = filteredPosts.map(post => {
  return {
    price: post.item_price,
    seller_name: post.seller_name || null,
    title: post.title || null,
    url: post.url || null,
  };
});

return [
  {
    json: {
      items: extracted
    }
  }
];

const rawHtml = $json.html;

return [{
  json: {
    html: `
      <html>
        <head>
          <style>
            body { font-family: Arial, sans-serif; line-height: 1.5; font-size: 15px; }
            h1, h2, h3 { color: #333; }
            a { color: #1a73e8; text-decoration: none; }
            ul { padding-left: 20px; }
            li { margin-bottom: 6px; }
          </style>
        </head>
        <body>
          ${rawHtml}
        </body>
      </html>
    `
  }
}];
