parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "search_results": {
      "type": "array",
      "description": "A list of search results related to the query.",
      "items": {
        "type": "object",
        "properties": {
          "title": {
            "type": "string",
            "description": "The title of the search result or webpage."
          },
          "url": {
            "type": "string",
            "format": "uri",
            "description": "The URL of the result."
          },
          "description": {
            "type": "string",
            "description": "A short description or snippet summarizing the content of the URL."
          }
        },
        "required": ["title", "url", "description"]
      }
    },
    "related_queries": {
      "type": "array",
      "description": "A list of queries related to the original search, often used for refining or expanding the search.",
      "items": {
        "type": "string"
      }
    }
  },
  "required": ["search_results"]
}

parameters.functionCode:
items[0].binary = {
  data: {
    data: new Buffer(JSON.stringify(items[0].json, null, 2)).toString('base64')
  }
};
return items;
