// Enhanced Weather Forecast Processor
// Processes OpenWeatherMap API data and formats for Telegram

function getWeatherEmoji(weatherMain, cloudiness = 0, isNight = false) {
 const emojiMap = {
   'Clear': isNight ? 'üåô' : '‚òÄÔ∏è',
   'Clouds': cloudiness > 75 ? '‚òÅÔ∏è' : cloudiness > 25 ? '‚õÖ' : 'üå§Ô∏è',
   'Rain': 'üåßÔ∏è',
   'Drizzle': 'üå¶Ô∏è',
   'Thunderstorm': '‚õàÔ∏è',
   'Snow': '‚ùÑÔ∏è',
   'Mist': 'üå´Ô∏è',
   'Fog': 'üå´Ô∏è',
   'Haze': 'üå´Ô∏è',
   'Smoke': 'üå´Ô∏è'
 };
 return emojiMap[weatherMain] || 'üå§Ô∏è';
}

function getTemperatureEmoji(temp) {
 if (temp >= 30) return 'üî•';
 if (temp >= 25) return 'üòé';
 if (temp >= 20) return 'üòä';
 if (temp >= 15) return 'üòê';
 if (temp >= 10) return 'üß•';
 if (temp >= 5) return 'ü•∂';
 return 'üßä';
}

function getWindInfo(windSpeed, windDir = null) {
 let windEmoji = 'üí®';
 let windText = '';

 if (windSpeed < 2) windText = 'Calm';
 else if (windSpeed < 6) windText = 'Light breeze';
 else if (windSpeed < 12) windText = 'Moderate breeze';
 else if (windSpeed < 20) windText = 'Strong breeze';
 else windText = 'Very windy';

 const windKmh = (windSpeed * 3.6).toFixed(1);
 return `${windEmoji} ${windText} (${windKmh} km/h)`;
}

function getHumidityLevel(humidity) {
 if (humidity >= 80) return 'üíß Very humid';
 if (humidity >= 60) return 'üíß Humid';
 if (humidity >= 40) return 'üíß Comfortable';
 return 'üèúÔ∏è Dry';
}

function getUVAdvice(hour) {
 // Estimate UV based on time (simplified)
 if (hour >= 10 && hour <= 16) return '‚òÄÔ∏è Consider sun protection';
 return '';
}

function formatDateTime(dtText) {
 const date = new Date(dtText);
 const timeStr = date.toLocaleTimeString('en-GB', { 
   hour: '2-digit', 
   minute: '2-digit',
   timeZone: 'Europe/Luxembourg'
 });
 return timeStr;
}

// Main processing
const forecast = items[0].json.list;
const cityName = items[0].json.city?.name || 'Strassen';
const country = items[0].json.city?.country || 'LU';
const timezone = items[0].json.city?.timezone || 0;

const now = new Date();
const today = now.toISOString().split('T')[0];
const desiredHours = ["09:00", "12:00", "15:00", "18:00", "21:00"];

let summary = '';
let headerIcon = 'üå§Ô∏è';
let dayHighTemp = -999;
let dayLowTemp = 999;
let totalRain = 0;
let avgHumidity = 0;
let avgWindSpeed = 0;
let weatherCount = 0;

// Process today's forecast
let firstMatch = true;
forecast.forEach(item => {
 const dt = item.dt_txt;
 const [date, time] = dt.split(' ');
 const hour = time.slice(0, 5);
 const hourNum = parseInt(time.slice(0, 2));

 if (date === today && desiredHours.includes(hour)) {
   const temp = Math.round(item.main.temp);
   const feelsLike = Math.round(item.main.feels_like);
   const humidity = item.main.humidity;
   const windSpeed = item.wind?.speed || 0;
   const rain = item.rain?.['3h'] || 0;
   const clouds = item.clouds?.all || 0;
   const weatherMain = item.weather?.[0]?.main || '';
   const weatherDesc = item.weather?.[0]?.description || '';
   const isNight = hourNum < 6 || hourNum > 20;

   // Track daily stats
   dayHighTemp = Math.max(dayHighTemp, temp);
   dayLowTemp = Math.min(dayLowTemp, temp);
   totalRain += rain;
   avgHumidity += humidity;
   avgWindSpeed += windSpeed;
   weatherCount++;

   // Set header icon based on first matching hour
   if (firstMatch && weatherMain) {
     headerIcon = getWeatherEmoji(weatherMain, clouds, isNight);
     firstMatch = false;
   }

   const tempEmoji = getTemperatureEmoji(temp);
   const weatherEmoji = getWeatherEmoji(weatherMain, clouds, isNight);
   const feelsLikeText = Math.abs(temp - feelsLike) > 2 ? ` (feels ${feelsLike}¬∞C)` : '';
   const rainText = rain > 0 ? `, üåßÔ∏è ${rain}mm` : '';
   const windText = windSpeed > 3 ? `, ${getWindInfo(windSpeed).split(' ')[0]} ${(windSpeed * 3.6).toFixed(0)} km/h` : '';
   const humidityText = humidity > 80 || humidity < 30 ? `, üíß ${humidity}%` : '';
   const uvText = getUVAdvice(hourNum);

   summary += `üïí <b>${hour}</b> ${weatherEmoji} ${temp}¬∞C${feelsLikeText}${rainText}${windText}${humidityText}\n`;
   if (uvText) summary += `    ${uvText}\n`;
 }
});

// Calculate averages
if (weatherCount > 0) {
 avgHumidity = Math.round(avgHumidity / weatherCount);
 avgWindSpeed = avgWindSpeed / weatherCount;
}

// Build comprehensive message
let message = `${headerIcon} <b>Weather Forecast for ${cityName}, ${country}</b>\n`;
message += `üìÖ ${now.toLocaleDateString('en-GB', { 
 weekday: 'long', 
 year: 'numeric', 
 month: 'long', 
 day: 'numeric',
 timeZone: 'Europe/Luxembourg'
})}\n\n`;

// Daily overview
message += `<b>üìä Daily Overview</b>\n`;
if (dayHighTemp > -999 && dayLowTemp < 999) {
 message += `üå°Ô∏è Range: ${dayLowTemp}¬∞C - ${dayHighTemp}¬∞C\n`;
}
if (totalRain > 0) {
 message += `üåßÔ∏è Expected rain: ${totalRain.toFixed(1)}mm\n`;
}
if (avgWindSpeed > 2) {
 message += `${getWindInfo(avgWindSpeed)}\n`;
}
message += `${getHumidityLevel(avgHumidity)} (${avgHumidity}%)\n\n`;

// Hourly forecast
message += `<b>‚è∞ Hourly Forecast</b>\n`;
message += summary;

// Smart recommendations - only show section if there are recommendations
let recommendations = '';
if (totalRain > 0) {
 recommendations += `‚òÇÔ∏è Bring an umbrella or raincoat\n`;
}
if (dayLowTemp < 10) {
 recommendations += `üß• Dress warmly (below 10¬∞C expected)\n`;
}
if (dayHighTemp > 25) {
 recommendations += `üíß Stay hydrated and consider sun protection\n`;
}
if (avgWindSpeed > 15) {
 recommendations += `üí® Windy conditions - secure loose items\n`;
}
if (avgHumidity > 80) {
 recommendations += `üí¶ High humidity - may feel warmer than actual temperature\n`;
}

// Only add recommendations section if there are any recommendations
if (recommendations) {
 message += `\n<b>üí° Recommendations</b>\n`;
 message += recommendations;
}

// Footer with data source and time
const updateTime = now.toLocaleTimeString('en-GB', {
 hour: '2-digit',
 minute: '2-digit',
 timeZone: 'Europe/Luxembourg'
});
message += `\n<i>üì° Data from OpenWeatherMap | Updated: ${updateTime} CET</i>`;

return [{ json: { message: message } }];
