parameters.inputSchema:
{
  "$schema": "http://json-schema.org/schema#",
  "title": "EcommerceSentimentSubset",
  "type": "object",
  "properties": {
    "sentiment": {
      "type": "string",
      "enum": ["positive", "neutral", "negative"],
      "description": "Categorized sentiment from NLP analysis"
    },
    "sentimentScore": {
      "type": "number",
      "minimum": -1,
      "maximum": 1,
      "description": "Numeric sentiment polarity score (-1 = very negative, 1 = very positive)"
    },
    "topics": {
      "type": "array",
      "description": "Key aspects mentioned in the review (e.g., battery, build quality, delivery)",
      "items": {
        "type": "string"
      }
    }
  }
}
