$input.item.json.fields = [
  {
    name: "Language",
    value: $json.language,
    inline: true,
  },
  {
    name: "Score",
    value: $json.score,
    inline: true,
  },
];

return $input.item;

switch ($json.description) {
  case "subtitles downloaded":
    $json.description = "Subtitles downloaded";
    break;
}

return $input.item;

switch ($json.description) {
  case "MovieFileDeleteupgrade":
    $json.description = "Movie upgraded";
    break;
  case "MovieFileDeletemanual":
    $json.description = "Movie file deleted";
    break;
  case "Download":
    $json.description = "Movie imported";
    break;
  case "ManualInteractionRequired":
    $json.description = "Manual interaction required";
    break;
}

return $input.item;

switch ($json.description) {
  case "EpisodeFileDeleteupgrade":
    $json.description = "Episode upgraded";
    break;
  case "EpisodeFileDeletemanual":
    $json.description = "Episode file deleted";
    break;
  case "Download":
    $json.description = "Episode Imported";
    break;
  case "ManualInteractionRequired":
    $json.description = "Manual interaction required";
    break;
}

return $input.item;

var content = {
  username: $json.username,
  avatar_url: $json.avatar,
  embeds: [
    {
      title: $json.title,
      description: $json.description,
      author: {
        name: $json.username,
        icon_url: $json.avatar,
      },
      thumbnail: {
        url: $json.image
      },
      color: $json.color,
      fields: $json.fields,
    },
  ],
};

return { json: { body: content } };
