// Get OCR results from the input
const ocrResults = $input.all().map((item) => item.json);

// Define fields to extract
const fields = [
  "Name",
  "Date of Birth",
  "Patient ID",
  "Date of Visit",
  "Referring Physician",
  "Department",
  "Symptoms",
  "Blood Pressure",
  "Heart Rate",
  "Temperature",
  "Lab Results",
  "Diagnosis",
  "Medications",
  "Next Appointment",
  "Notes",
];

const allPatientRecords = [];

// Process each OCR result
ocrResults.forEach((result) => {
  // Combine all pages into a single text for processing
  let allText = "";
  if (result.pages && Array.isArray(result.pages)) {
    result.pages.forEach((page) => {
      if (page.markdown) {
        allText += page.markdown + "\n";
      }
    });
  }

  // Split the text into patient record sections
  // Look for patterns like "Patient Record 1", "ðŸ§¾ Patient Record 2", etc.
  const recordSections = allText.split(/(?:ðŸ§¾\s*)?Patient Record\s*\d+/g).filter(section => section.trim().length > 0);
  console.log(`Found ${recordSections.length} potential patient record sections`);

  // Process each patient record section
  recordSections.forEach((section, index) => {
    // Create a new patient record object
    const patientRecord = {};

    // Add Patient Name which has a different pattern than other fields
    const nameMatch = section.match(/Patient Name:\s*([^\n]+)/);
    if (nameMatch) {
      patientRecord["Name"] = nameMatch[1].trim();
    }

    // Extract all other fields
    fields.forEach((field) => {
      if (field === "Name") return; // Already handled above

      // Create a regex pattern for the field
      const pattern = new RegExp(`${field}:\\s*([^\\n]+)`, 'i');
      const match = section.match(pattern);

      if (match) {
        patientRecord[field] = match[1].trim();
      }

      // Special handling for Lab Results which might span multiple lines
      if (field === "Lab Results" && !patientRecord[field]) {
        const labStartIndex = section.indexOf("Lab Results:");
        if (labStartIndex !== -1) {
          let labEndIndex = -1;

          // Find where lab results end (before Diagnosis or next field)
          for (const nextField of ["Diagnosis", "Medications", "Next Appointment"]) {
            const nextFieldIndex = section.indexOf(`${nextField}:`, labStartIndex);
            if (nextFieldIndex !== -1 && (labEndIndex === -1 || nextFieldIndex < labEndIndex)) {
              labEndIndex = nextFieldIndex;
            }
          }

          if (labEndIndex !== -1) {
            let labResults = section.substring(labStartIndex + "Lab Results:".length, labEndIndex).trim();
            // Clean up lab results
            labResults = labResults.replace(/â—/g, '').replace(/\n\s*/g, ', ').trim();
            patientRecord[field] = labResults;
          }
        }
      }
    });

    // Only add records that have at least a name or patient ID
    if (patientRecord["Name"] || patientRecord["Patient ID"]) {
      console.log(`Extracted data for patient ${index + 1}: ${patientRecord["Name"] || patientRecord["Patient ID"]}`);
      allPatientRecords.push(patientRecord);
    }
  });
});

console.log(`Total patient records extracted: ${allPatientRecords.length}`);

// Return all extracted patient records
return allPatientRecords;
