const result = {};
let counter = 1;

for (const item of items) {
  try {
    const response = item.json.body || item.json;
    const metadata = response?.ListAccessKeysResponse?.ListAccessKeysResult?.AccessKeyMetadata || [];

    for (const key of metadata) {
      const keyLabel = `AccessKey${counter}`;
      result[keyLabel] = {
        [`AccessKeyId${counter}`]: key.AccessKeyId,
        [`UserName${counter}`]: key.UserName,
        Status: key.Status,
        [`CreateDate${counter}`]: new Date(key.CreateDate * 1000).toISOString()
      };
      counter++;
    }
  } catch (error) {
    console.error('Error processing access keys:', error);
  }
}

return [{ json: result }];

// Sample input (you'll usually get this from n8n's input)
const inputItems = $input.all();

const decodedPolicies = inputItems.map(item => {
  const encoded = item.json?.GetUserPolicyResponse?.GetUserPolicyResult?.PolicyDocument;
  let decoded = null;
  let parsed = null;

  try {
    decoded = decodeURIComponent(encoded);
    parsed = JSON.parse(decoded);
  } catch (e) {
    // Fallback if decoding or parsing fails
    parsed = { error: 'Invalid or undecodable policy document' };
  }

  return {
    json: {
      user: item.json?.GetUserPolicyResponse?.GetUserPolicyResult?.UserName || 'unknown',
      policyName: item.json?.GetUserPolicyResponse?.GetUserPolicyResult?.PolicyName || 'unknown',
      decodedPolicy: parsed
    }
  };
});

return decodedPolicies;

const inputItems = $input.all();

const decodedPolicies = inputItems.map(item => {
  const encoded = item.json?.GetPolicyVersionResponse?.GetPolicyVersionResult?.PolicyVersion?.Document;
  let decoded = null;
  let parsed = null;

  try {
    decoded = decodeURIComponent(encoded);
    parsed = JSON.parse(decoded);
  } catch (e) {
    parsed = { error: 'Invalid or undecodable policy document' };
  }

  return {
    json: {
      versionId: item.json?.GetPolicyVersionResponse?.GetPolicyVersionResult?.PolicyVersion?.VersionId || 'unknown',
      isDefault: item.json?.GetPolicyVersionResponse?.GetPolicyVersionResult?.PolicyVersion?.IsDefaultVersion || false,
      createDate: item.json?.GetPolicyVersionResponse?.GetPolicyVersionResult?.PolicyVersion?.CreateDate || null,
      decodedPolicy: parsed
    }
  };
});

return decodedPolicies;

