/**
 * Escapes control characters (like \n, \r, \t) within JSON strings.
 * Ex: Replaces a literal newline in "hello\nworld" with "hello\\nworld".
 * @param {string} jsonString The string potentially containing unescaped control characters.
 * @returns {string} The corrected string.
 */
function escapeControlCharsInStrings(jsonString) {
    let fixed = "";
    let inString = false;
    let escapeNext = false;

    for (let i = 0; i < jsonString.length; i++) {
        const char = jsonString[i];

        if (escapeNext) {
            fixed += char;
            escapeNext = false;
            continue;
        }

        if (char === '\\') {
            fixed += '\\';
            escapeNext = true;
            continue;
        }

        if (char === '"') {
            fixed += '"';
            inString = !inString;
            continue;
        }

        if (inString) {
            if (char === '\n') {
                fixed += '\\n';
            } else if (char === '\r') {
                fixed += '\\r';
            } else if (char === '\t') {
                fixed += '\\t';
            } else if (char === '\b') {
                fixed += '\\b';
            } else if (char === '\f') {
                fixed += '\\f';
            }
            // Handle other control characters (U+0000 to U+001F)
            // that do not have a short escape sequence.
            else if (char.charCodeAt(0) >= 0 && char.charCodeAt(0) <= 31) {
                let hex = char.charCodeAt(0).toString(16);
                fixed += '\\u' + '0000'.substring(hex.length) + hex;
            }
            else {
                fixed += char;
            }
        } else {
            fixed += char;
        }
    }
    return fixed;
}


/**
 * Fixes invalid escape sequences in a potential JSON string
 * by escaping the backslash itself.
 * Ex: Replaces `\$` with `\\$`, `\: ` with `\\:`, but leaves `\"` or `\\` or `\n` (if already escaped) intact.
 * @param {string} jsonString The string potentially containing invalid escapes.
 * @returns {string} The corrected string.
 */
function fixInvalidEscapes(jsonString) {
    let fixed = "";
    for (let i = 0; i < jsonString.length; i++) {
        if (jsonString[i] === '\\') {
            if (i + 1 < jsonString.length) {
                const nextChar = jsonString[i + 1];
                const validEscapeChars = ['"', '\\', '/', 'b', 'f', 'n', 'r', 't'];

                if (validEscapeChars.includes(nextChar)) {
                    fixed += '\\' + nextChar;
                    i++;
                } else if (nextChar === 'u') {
                    if (i + 5 < jsonString.length && /^[0-9a-fA-F]{4}$/.test(jsonString.substring(i + 2, i + 6))) {
                        fixed += '\\u' + jsonString.substring(i + 2, i + 6);
                        i += 5;
                    } else {
                        fixed += '\\\\u';
                        i++;
                    }
                } else {
                    fixed += '\\\\' + nextChar;
                    i++;
                }
            } else {
                fixed += '\\\\';
            }
        } else {
            fixed += jsonString[i];
        }
    }
    return fixed;
}

/**
 * Attempts to parse a JSON string. Returns the object/array or null on error.
 * Also captures the error message with context.
 * @param {string} jsonString The string to parse.
 * @returns {{ data: object | Array | null, error: string | null }} Parsing result.
 */
function tryParseJsonInternal(jsonString) {
    try {
        const data = JSON.parse(jsonString);
        if (typeof data === 'object' && data !== null) {
            return { data: data, error: null };
        } else {
            return { data: null, error: "Parsed data is not a JSON object or array." };
        }
    } catch (e) {
        let errorMessage = e.message;
        if (e.message.includes('position')) {
             try {
                 const positionMatch = e.message.match(/position\s+(\d+)/);
                 if (positionMatch && positionMatch[1]) {
                      const position = parseInt(positionMatch[1], 10);
                      const contextChars = 20;
                      const start = Math.max(0, position - contextChars);
                      const end = Math.min(jsonString.length, position + contextChars);
                      const context = jsonString.substring(start, end);
                      errorMessage += ` (near: ...${context}...)`;
                 }
             } catch (contextError) { /* Ignore error during context generation */ }
        }
        return { data: null, error: errorMessage };
    }
}


/**
 * Attempts to fix unescaped double quotes within JSON strings.
 * @param {string} jsonString The potentially malformed JSON string.
 * @returns {string} The JSON string with an attempted quote fix.
 */
function fixJsonStringQuotes(jsonString) {
    let fixed = "";
    let inString = false;
    let escapeNext = false;

    for (let i = 0; i < jsonString.length; i++) {
        const char = jsonString[i];

        if (escapeNext) { fixed += char; escapeNext = false; continue; }
        if (char === '\\') { fixed += '\\'; escapeNext = true; continue; }
        if (char === '"') {
            if (inString) {
                let nextMeaningfulChar = null;
                for (let j = i + 1; j < jsonString.length; j++) {
                    if (!/\s/.test(jsonString[j])) { nextMeaningfulChar = jsonString[j]; break; }
                }
                if (nextMeaningfulChar === null || [':', ',', '}', ']'].includes(nextMeaningfulChar)) {
                    fixed += '"'; inString = false;
                } else { fixed += '\\"'; } // Escape internal quote
            } else { fixed += '"'; inString = true; }
        } else { fixed += char; }
    }
    return fixed;
}


/**
 * Extracts potential JSON objects or arrays based on balancing { } and [ ] characters.
 * @param {string} text The source text.
 * @returns {string[]} An array of potentially JSON strings.
 */
function extractPotentialJsonStrings(text) {
    const results = [];
    let braceDepth = 0, bracketDepth = 0, startIndex = -1, inString = false, escapeNext = false;
    for (let i = 0; i < text.length; i++) {
        const char = text[i];
        if (escapeNext) { escapeNext = false; continue; }
        if (char === '\\') { escapeNext = true; continue; }
        if (char === '"') { inString = !inString; }
        if (!inString) {
            if (char === '{') { if (braceDepth === 0 && bracketDepth === 0) startIndex = i; braceDepth++; }
            else if (char === '}') { if (braceDepth > 0) { braceDepth--; if (braceDepth === 0 && bracketDepth === 0 && startIndex !== -1) { results.push(text.substring(startIndex, i + 1)); startIndex = -1; } } }
            else if (char === '[') { if (braceDepth === 0 && bracketDepth === 0) startIndex = i; bracketDepth++; }
            else if (char === ']') { if (bracketDepth > 0) { bracketDepth--; if (braceDepth === 0 && bracketDepth === 0 && startIndex !== -1) { results.push(text.substring(startIndex, i + 1)); startIndex = -1; } } }
        }
    }
    return results;
}

/**
 * Attempts to fix trailing commas.
 * @param {string} jsonString JSON string potentially with trailing commas.
 * @returns {string} Corrected string.
 */
function fixTrailingCommas(jsonString) {
    try { return jsonString.replace(/,\s*([}\]])/g, '$1'); }
    catch(e) { return jsonString; }
}

/**
 * Main function to find and parse the first valid JSON object/array in a text,
 * with pre-cleaning and multiple correction attempts.
 * Merges the keys of the found JSON with parsing metadata.
 *
 * @param {string} text The text potentially containing JSON.
 * @returns {object} An object containing the keys of the parsed JSON (if object)
 *                   or a 'parsed_data' key (if array), and a 'parsing_status' object.
 */
function findAndParseJsonRobustly(text) {
    let firstParsedData = null;
    const parsing_status = {
        success: false,
        successfulMethod: null,
        controlCharFixApplied: false, // New flag
        invalidEscapeFixApplied: false,
        trailingCommaFixApplied: false,
        quoteFixApplied: false,
        parsingLog: [],
        errors: [],
        originalInput: text,
        cleanedInput: null,
        finalAttemptedJsonString: null
    };

    if (!text || typeof text !== 'string') {
        parsing_status.errors.push("Input text is null or not a string.");
        parsing_status.parsingLog.push("Validation failed: Input is not a valid string.");
        return { parsing_status };
    }

    parsing_status.parsingLog.push("Starting robust JSON parsing.");

    let currentText = text.trim();
    parsing_status.parsingLog.push(`Original length: ${text.length}, Trimmed length: ${currentText.length}`);
    const markdownBlockRegex = /^```(?:json)?\s*([\s\S]*?)\s*```$/;
    const match = currentText.match(markdownBlockRegex);
    if (match && match[1]) {
        currentText = match[1].trim();
        parsing_status.parsingLog.push("Detected and removed Markdown code block fences.");
    } else {
        parsing_status.parsingLog.push("No Markdown code block fences detected.");
    }
    parsing_status.cleanedInput = currentText;
    parsing_status.parsingLog.push(`Cleaned text length after potential fence removal: ${currentText.length}`);

    function processSuccess(data, method, flags) {
        parsing_status.parsingLog.push(`Success with method: ${method}`);
        parsing_status.success = true;
        parsing_status.successfulMethod = method;
        parsing_status.controlCharFixApplied = flags.controlCharFixApplied || false;
        parsing_status.invalidEscapeFixApplied = flags.invalidEscapeFixApplied || false;
        parsing_status.trailingCommaFixApplied = flags.trailingCommaFixApplied || false;
        parsing_status.quoteFixApplied = flags.quoteFixApplied || false;
        firstParsedData = data;
    }

    function attemptParseAndFix(textToParse, baseMethodName) {
        let currentAttemptText = textToParse;
        let flags = { controlCharFixApplied: false, invalidEscapeFixApplied: false, trailingCommaFixApplied: false, quoteFixApplied: false };
        let method = baseMethodName;
        let parseResult;

        // 0. Escape control characters WITHIN strings (BEFORE fixInvalidEscapes)
        parsing_status.parsingLog.push(`[${baseMethodName}] Applying control character escaping in strings...`);
        let fixedControlCharsText = escapeControlCharsInStrings(currentAttemptText);
        if (fixedControlCharsText !== currentAttemptText) {
            parsing_status.parsingLog.push(`[${baseMethodName}] Control characters in strings escaped.`);
            flags.controlCharFixApplied = true;
            currentAttemptText = fixedControlCharsText;
            method = `${baseMethodName} + Control Char Escape`;
        } else {
            parsing_status.parsingLog.push(`[${baseMethodName}] No control characters in strings needed escaping or found.`);
        }
        parsing_status.finalAttemptedJsonString = currentAttemptText;
        parseResult = tryParseJsonInternal(currentAttemptText);
        if (parseResult.data) { processSuccess(parseResult.data, method, flags); return true; }
        parsing_status.parsingLog.push(`[${baseMethodName}] Parse failed after control char escape (if any): ${parseResult.error}`);
        parsing_status.errors.push(`Parse (${method}): ${parseResult.error}`);


        // 1. Fix invalid escapes (backslash)
        parsing_status.parsingLog.push(`[${baseMethodName}] Applying invalid escape fix (escaping backslash)...`);
        let fixedEscapesText = fixInvalidEscapes(currentAttemptText);
        if (fixedEscapesText !== currentAttemptText) {
            parsing_status.parsingLog.push(`[${baseMethodName}] Invalid backslash escapes fixed.`);
            flags.invalidEscapeFixApplied = true;
            currentAttemptText = fixedEscapesText;
            method = `${baseMethodName}${flags.controlCharFixApplied ? ' + Control Char Escape' : ''} + Invalid Escape Fix`;
        } else {
            parsing_status.parsingLog.push(`[${baseMethodName}] No invalid backslash escapes found/fixed.`);
        }
        parsing_status.finalAttemptedJsonString = currentAttemptText;
        parseResult = tryParseJsonInternal(currentAttemptText);
        if (parseResult.data) { processSuccess(parseResult.data, method, flags); return true; }
        parsing_status.parsingLog.push(`[${baseMethodName}] Parse failed after invalid escape fix (if any): ${parseResult.error}`);
        parsing_status.errors.push(`Parse (${method}): ${parseResult.error}`);


        // 2. Fix trailing commas
        parsing_status.parsingLog.push(`[${baseMethodName}] Applying trailing comma fix...`);
        let fixedCommasText = fixTrailingCommas(currentAttemptText);
        if (fixedCommasText !== currentAttemptText) {
            parsing_status.parsingLog.push(`[${baseMethodName}] Trailing commas fixed.`);
            flags.trailingCommaFixApplied = true;
            currentAttemptText = fixedCommasText;
            method = `${baseMethodName}${flags.controlCharFixApplied ? ' + Control Char Escape' : ''}${flags.invalidEscapeFixApplied ? ' + Invalid Escape Fix' : ''} + Trailing Comma Fix`;
            parsing_status.finalAttemptedJsonString = currentAttemptText;
            parseResult = tryParseJsonInternal(currentAttemptText);
            if (parseResult.data) { processSuccess(parseResult.data, method, flags); return true; }
            parsing_status.parsingLog.push(`[${baseMethodName}] Parse failed after comma fix: ${parseResult.error}`);
            parsing_status.errors.push(`Parse (${method}): ${parseResult.error}`);
        } else {
            parsing_status.parsingLog.push(`[${baseMethodName}] No trailing commas found/fixed.`);
        }

        // 3. Fix quotes
        parsing_status.parsingLog.push(`[${baseMethodName}] Applying quote fix...`);
        try {
            let fixedQuotesText = fixJsonStringQuotes(currentAttemptText);
            if (fixedQuotesText !== currentAttemptText) {
                parsing_status.parsingLog.push(`[${baseMethodName}] Quotes fixed.`);
                flags.quoteFixApplied = true;
                currentAttemptText = fixedQuotesText;
                method = `${baseMethodName}${flags.controlCharFixApplied ? ' + Control Char Escape' : ''}${flags.invalidEscapeFixApplied ? ' + Invalid Escape Fix' : ''}${flags.trailingCommaFixApplied ? ' + Trailing Comma Fix' : ''} + Quote Fix`;
                parsing_status.finalAttemptedJsonString = currentAttemptText;
                parseResult = tryParseJsonInternal(currentAttemptText);
                if (parseResult.data) { processSuccess(parseResult.data, method, flags); return true; }
                parsing_status.parsingLog.push(`[${baseMethodName}] Parse failed after quote fix: ${parseResult.error}`);
                parsing_status.errors.push(`Parse (${method}): ${parseResult.error}`);
            } else {
                parsing_status.parsingLog.push(`[${baseMethodName}] Quote fixing did not modify the text.`);
            }
        } catch (e) {
            parsing_status.parsingLog.push(`[${baseMethodName}] Error during quote fixing attempt: ${e.message}`);
            parsing_status.errors.push(`Parse (${method} - quote fix error): ${e.message}`);
        }

        parsing_status.finalAttemptedJsonString = currentAttemptText;
        return false;
    }

    parsing_status.parsingLog.push("Attempting parse/fix on entire cleaned text...");
    attemptParseAndFix(parsing_status.cleanedInput, "Direct");

    if (!firstParsedData) {
        parsing_status.parsingLog.push("Direct processing failed. Attempting structured extraction...");
        const candidates = extractPotentialJsonStrings(parsing_status.cleanedInput);
        parsing_status.parsingLog.push(`Found ${candidates.length} potential JSON candidate(s) via balancing.`);

        for (let i = 0; i < candidates.length && !firstParsedData; i++) {
            const candidate = candidates[i];
            parsing_status.parsingLog.push(`Attempting parse/fix on candidate ${i + 1}/${candidates.length}...`);
            attemptParseAndFix(candidate, `Structured Candidate ${i + 1}`);
        }
    }

    if (firstParsedData) {
        parsing_status.parsingLog.push(`Successfully parsed JSON found using method: ${parsing_status.successfulMethod}.`);
        if (typeof firstParsedData === 'object' && !Array.isArray(firstParsedData)) {
             return { ...firstParsedData, parsing_status: parsing_status };
        } else {
             parsing_status.parsingLog.push("Parsed data is an array or non-object, returning under 'parsed_data' key.");
             return { parsed_data: firstParsedData, parsing_status: parsing_status };
        }
    } else {
        parsing_status.parsingLog.push("No valid JSON object or array could be parsed from the input after all attempts.");
        parsing_status.success = false;
        if (!parsing_status.finalAttemptedJsonString && parsing_status.cleanedInput) {
             parsing_status.finalAttemptedJsonString = parsing_status.cleanedInput;
        } else if (!parsing_status.finalAttemptedJsonString) {
             parsing_status.finalAttemptedJsonString = text;
        }
        return { parsing_status: parsing_status };
    }
}

// --- n8n Usage ---
const inputItem = $input.item.json; 
const inputText = inputItem && inputItem.text ? inputItem.text : null;
const parsingResult = findAndParseJsonRobustly(inputText);

return parsingResult;
