const payTo = $input.first().json.payTo;
const timeOut = $input.first().json.timeOut;
const resourceDescription = $input.first().json.resourceDescription;
const paymentTokens = $input.first().json.paymentTokens;

// this function will split the signature from the user into r,s & v components
function splitSignature(sigHex) {
  // Remove "0x" prefix if present
  const hex = sigHex.startsWith("0x") ? sigHex.slice(2) : sigHex;

  // Convert hex to byte array
  const bytes = new Uint8Array(hex.length / 2);
  for (let i = 0; i < bytes.length; i++) {
    bytes[i] = parseInt(hex.substr(i * 2, 2), 16);
  }

  if (bytes.length !== 65) {
    throw new Error(`Invalid signature length: got ${bytes.length} bytes`);
  }

  // Convert byte ranges to hex strings
  const toHex = (arr) =>
    "0x" + Array.from(arr).map(b => b.toString(16).padStart(2, "0")).join("");

  const r = toHex(bytes.slice(0, 32));
  const s = toHex(bytes.slice(32, 64));

  // Normalize v
  let v = bytes[64];
  if (v === 0) v = 27;
  else if (v === 1) v = 28;
  else if (v !== 27 && v !== 28 && v >= 35) {
    v = (v & 1) ? 27 : 28; // EIP-155
  }

  const vHex = "0x" + v.toString(16).padStart(2, "0");

  return { r, s, v: vHex };
}

// this will make sure our x-payment header contains all necessary components
function validateXPayment(obj) {
  // Define the expected structure and types
  const requiredShape = {
    x402Version: "number",
    scheme: "string",
    network: "string",
    payload: {
      authorization: {
        from: "string",
        to: "string",
        value: "string",
        validAfter: "string",
        validBefore: "string",
        nonce: "string"
      },
      signature: "string"
    }
  };

  const missing = [];

  function checkShape(expected, actual, path) {
    for (const key in expected) {
      const currentPath = path ? path + "." + key : key;

      if (!(key in actual)) {
        missing.push("Missing field: " + currentPath);
      } else if (typeof expected[key] === "object") {
        if (typeof actual[key] !== "object" || actual[key] === null) {
          missing.push("Invalid type at " + currentPath + ": expected object");
        } else {
          checkShape(expected[key], actual[key], currentPath);
        }
      } else {
        if (typeof actual[key] !== expected[key]) {
          missing.push(
            "Invalid type at " + currentPath +
            ": expected " + expected[key] +
            ", got " + typeof actual[key]
          );
        }
      }
    }
  }

  checkShape(requiredShape, obj, "");

  if (missing.length > 0) {
    return missing.join("; ");
  }
  return "valid";
}

// this function will ensure the x-payment header is for one of our supported
// networks, is for the correct amount, and pays the right address
function verifyPaymentDetails(obj, config, payTo) {
  const errors = [];

  // 1. Check that network exists in config
  const network = obj.network;
  const configEntry = Object.values(config).find(
    (entry) => entry.network.toLowerCase() === (network || "").toLowerCase()
  );

  if (!configEntry) {
    errors.push("Invalid or unsupported network: " + network);
  }

  // 2. Check value >= maxAmountRequired
  if (configEntry) {
    const required = BigInt(configEntry.maxAmountRequired);
    let actual;

    try {
      actual = BigInt(obj.payload.authorization.value);
    } catch (e) {
      errors.push("Invalid value: must be numeric string");
    }

    if (typeof actual !== "undefined" && actual < required) {
      errors.push(
        `Value too low: got ${actual}, requires at least ${required}`
      );
    }
  }

  // 3. Check 'to' matches payTo (case-insensitive)
  const toAddr = obj.payload?.authorization?.to;
  if (!toAddr) {
    errors.push("Missing 'to' field in authorization");
  } else if (toAddr.toLowerCase() !== payTo.toLowerCase()) {
    errors.push(`Invalid 'to' address: expected ${payTo}, got ${toAddr}`);
  }

  return errors.length > 0 ? errors.join("; ") : "valid";
}


// this helper function will convert our simple payment configs into an x402 
// error response 
function transformConfig(config, payTo, timeout, resource, resourceDescription) {
  return Object.values(config).map(entry => ({
    scheme: "exact",
    network: entry.network,
    maxAmountRequired: entry.maxAmountRequired,
    resource: resource, 
    description: resourceDescription,
    mimeType: "",
    payTo: payTo,
    maxTimeoutSeconds: timeout, // passed in as argument
    asset: entry.tokenAddress,
    extra: {
      name: entry.name,
      version: entry.version
    }
  }));
}

const accepts = transformConfig(paymentTokens, payTo, timeOut, $input.first().json.webhookUrl, resourceDescription);
$input.first().json.accepts = accepts;

// try to decode the x-payment header if it exists
try {
    // Decode the x-payment header from base64
    const xPaymentHeader = $input.first().json.headers['x-payment'];
    const decodedXPayment = Buffer.from(xPaymentHeader, 'base64').toString('utf-8');

    // Parse the decoded value into a JSON object
    const decodedXPaymentJson = JSON.parse(decodedXPayment);

    const validation = validateXPayment(decodedXPaymentJson)
    if (validation != "valid") {
      return { 
        error: {
          errorMessage: validation, 
          paymentConfigs: accepts
        } 
      };
    }

    const verification = verifyPaymentDetails(decodedXPaymentJson, paymentTokens, payTo);
    if (verification != "valid") {
      return {
        error: {
          errorMessage: verification,
          paymentConfigs: accepts
        }
      }
    }

    // Add the parsed JSON object to the input
    $input.first().json.decodedXPayment = decodedXPaymentJson;
    console.log("asdf:", decodedXPaymentJson)

    const splitSig = splitSignature(decodedXPaymentJson.payload.signature);
    $input.first().json.decodedXPayment.payload.r = splitSig.r;
    $input.first().json.decodedXPayment.payload.s = splitSig.s;
    $input.first().json.decodedXPayment.payload.v = splitSig.v;

    $input.first().json.contractMethodId = Object.values(paymentTokens).find(
      (entry) => entry.network.toLowerCase() === (decodedXPaymentJson.network || "").toLowerCase()
     ).contractMethodId;
    console.log("asdf", $input.first().json.contractMethodId)

    return $input.all();
} catch (error) {
    // Return an error object if the token format is invalid
    return { 
      error: {
        errorMessage: "Could not decode x-payment header", 
        paymentConfigs: accepts
      } 
    };
}

return $input.all();

