// n8n Function Node

// 1. Grab GA records
const gaRecords   = $('gaData').first().json.gaData;
const discord_channel_id   = $('Code1').first().json.discord_channel_id;

// 2. Grab Discord messages and unpack their .json
const discordMsgs = $input.all().map(item => item.json);

// Helper: get embed field by name
function getEmbedField(msg, name) {
  return msg.embeds?.[0]?.fields.find(f => f.name === name)?.value || null;
}

// Format JS Date to dd-MM-yyyy
function formatDate(dateObj) {
  const dd = String(dateObj.getDate()).padStart(2,'0');
  const mm = String(dateObj.getMonth()+1).padStart(2,'0');
  const yyyy = dateObj.getFullYear();
  return `${dd}-${mm}-${yyyy}`;
}

const today = formatDate(new Date());

// Build lookup of Discord messages by Date
const discordByDate = {};
for (const msg of discordMsgs) {
  const date = getEmbedField(msg, 'Date');
  if (date) discordByDate[date] = msg;
}

const results = [];

for (const wrapper of gaRecords) {
  const rec = wrapper.json;
  const dateKey = rec.date;
  const existing = discordByDate[dateKey];

  // Prepare new fields
  const newFields = [
    { name: 'Date',                  value: rec.date,                    inline: true },
    { name: 'totalUsers',           value: rec.totalUsers,             inline: true },
    { name: 'active1DayUsers',      value: rec.active1DayUsers,        inline: true },
    { name: 'active7DayUsers',      value: rec.active7DayUsers,        inline: true },
    { name: 'eventCount',           value: rec.eventCount,             inline: true },
    { name: 'screenPageViews',      value: rec.screenPageViews,        inline: true },
    { name: 'userEngagementDuration', value: rec.userEngagementDuration, inline: true },
    { name: 'createdAt',            value: existing ? getEmbedField(existing, 'createdAt') : today, inline: true },
    { name: 'updatedAt',            value: today,                       inline: true },
  ];

  // If exists, compare fields to avoid unnecessary updates
  if (existing) {
    let changed = false;
    for (const field of newFields) {
      if (getEmbedField(existing, field.name) !== field.value) {
        changed = true;
        break;
      }
    }
    if (changed) {
      results.push({
        action:    'update',
        messageId: existing.id,
        channelId: discord_channel_id,
        payload: {
          content: '',
          embeds: [{
            type:   'rich',
            color:  16751872,
            fields: newFields,
            footer: {
              text:     'Google Analytics',
              icon_url: 'https://images-ext-1.discordapp.net/external/BO599wb-y-yg7GM59J_ZSexsGRd2zIqOF98eJ89J_04/https/www.gstatic.com/analytics-suite/header/suite/v2/ic_analytics.svg'
            }
          }]
        }
      });
    }
  } else {
    // New record ‚Üí create
    results.push({
      action:  'create',
      channelId: discord_channel_id,
      payload: {
        content: '',
        embeds: [{
          type:   'rich',
          color:  16751872,
          fields: newFields,
          footer: {
            text:     'Google Analytics',
            icon_url: 'https://images-ext-1.discordapp.net/external/BO599wb-y-yg7GM59J_ZSexsGRd2zIqOF98eJ89J_04/https/www.gstatic.com/analytics-suite/header/suite/v2/ic_analytics.svg'
          }
        }]
      }
    });
  }
}

// Return array for next nodes
return results.map(r => ({ json: r }));
