// Get HTML content from current input
const htmlContent = $input.first().json.message.content;

// Generate filename with current date
const currentDate = new Date().toISOString().split('T')[0];
const filename = `document_${currentDate}.html`;

// Convert string to binary buffer
const buffer = Buffer.from(htmlContent, 'utf8');

return [{
  json: {
    fileName: filename,
    mimeType: 'text/html'
  },
  binary: {
    data: buffer
  }
}];
