const marked = require('marked');

function convertMarkdownToHtml(obj, propertiesToConvert) {
  // Check if the object is an array
  if (Array.isArray(obj)) {
    // If it's an array, loop through each item and recursively call the function
    return obj.map(item => convertMarkdownToHtml(item, propertiesToConvert));
  } else if (typeof obj === 'object') {
    // If it's an object, loop through each property
    for (const key in obj) {
      if (Object.prototype.hasOwnProperty.call(obj, key)) {
        const value = obj[key];
        // Check if the property name is in the array of properties to convert
        if (propertiesToConvert.includes(key)) {
          // Check if the value is a string (potential markdown)
          if (typeof value === 'string') {
            // Convert markdown to HTML using marked library
            obj[key] = marked.parse(value);
          }
        } else {
          // If the property name is not in the array, recursively check if it's an object or array
          if (typeof value === 'object') {
            obj[key] = convertMarkdownToHtml(value, propertiesToConvert);
          }
        }
      }
    }
    return obj;
  } else {
    // If it's not an object or array, return as is (since we can't convert it)
    return obj;
  }
}

