// Get the AI agent data (latest data)
const aiAgentData = $('ðŸ¤– AI Agent: Scrape Tesla Financial Data').first().json.output.financial_data;

// Get the Google Sheet data (old data)
const googleSheetData = items[0].json; // Assuming Google Sheet data is in the first item

// Prepare the comparison result
const comparisonResult = {
  "comparison": [
    {
      "metric": "Total Revenue",
      "your_company_data": `$${googleSheetData['Total Revenue']}`,
      "tesla_data": aiAgentData.revenue.total_revenue,
      "result": aiAgentData.revenue.total_revenue > `$${googleSheetData['Total Revenue']}` ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Quarter Estimate",
      "your_company_data": `$${googleSheetData['Quarter Estimate']}`,
      "tesla_data": aiAgentData.revenue.quarter_value_estimate,
      "result": aiAgentData.revenue.quarter_value_estimate > `$${googleSheetData['Quarter Estimate']}` ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Pretax Income",
      "your_company_data": `$${googleSheetData['Pretax Income']}`,
      "tesla_data": aiAgentData.total_income.pretax_income,
      "result": aiAgentData.total_income.pretax_income > `$${googleSheetData['Pretax Income']}` ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Basic EPS",
      "your_company_data": googleSheetData['Basic EPS'],
      "tesla_data": aiAgentData.earnings_per_share.basic_eps,
      "result": aiAgentData.earnings_per_share.basic_eps > googleSheetData['Basic EPS'] ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Diluted EPS",
      "your_company_data": googleSheetData['Diluted EPS'],
      "tesla_data": aiAgentData.earnings_per_share.diluted_eps,
      "result": aiAgentData.earnings_per_share.diluted_eps > googleSheetData['Diluted EPS'] ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Net Income",
      "your_company_data": `$${googleSheetData['Net Income']}`,
      "tesla_data": aiAgentData.profit.net_income,
      "result": aiAgentData.profit.net_income > `$${googleSheetData['Net Income']}` ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Net Margin",
      "your_company_data": `${googleSheetData['Net Margin'] * 100}%`,
      "tesla_data": aiAgentData.margins.net_margin,
      "result": parseFloat(aiAgentData.margins.net_margin) > googleSheetData['Net Margin'] ? "Outperforming" : "Underperforming"
    },
    {
      "metric": "Total Expenses",
      "your_company_data": `$${googleSheetData['Total Expenses']}`,
      "tesla_data": aiAgentData.total_expenses.total_expenses,
      "result": parseFloat(aiAgentData.total_expenses.total_expenses.replace(/[^\d.-]/g, '')) < parseFloat(googleSheetData['Total Expenses']) ? "Outperforming" : "Underperforming"
    }
  ]
};

// Return the comparison result
return [
  {
    json: comparisonResult
  }
];

