return items.map(item => {
  const raw = item.json?.result?.content?.[0]?.text;

  if (!raw) {
    return { json: { error: "No raw content", original: item.json } };
  }

  let data;
  try {
    data = typeof raw === "string" ? JSON.parse(raw) : raw;
  } catch (e) {
    return { json: { error: "Invalid JSON", original: raw } };
  }

  if (!data || typeof data !== 'object') {
    return { json: { error: "Parsed data invalid", original: raw } };
  }

  const listingUrl = data.listingUrl || "";
  const idMatch = listingUrl.match(/\/rooms\/(\d+)/);
  const id = idMatch ? idMatch[1] : null;

  const sections = Array.isArray(data.details) ? data.details : [];

  let lat = null;
  let lng = null;
  let houseRules = null;
  let highlights = null;
  let description = null;
  let amenities = null;

  sections.forEach(section => {
    switch (section.id) {
      case "LOCATION_DEFAULT":
        lat = section.lat || null;
        lng = section.lng || null;
        break;
      case "POLICIES_DEFAULT":
        houseRules = section.houseRulesSections || null;
        break;
      case "HIGHLIGHTS_DEFAULT":
        highlights = section.highlights || null;
        break;
      case "DESCRIPTION_DEFAULT":
        description = section.htmlDescription?.htmlText || null;
        break;
      case "AMENITIES_DEFAULT":
        amenities = section.seeAllAmenitiesGroups || null;
        break;
    }
  });

  return {
    json: {
      id: id || "",
      listingUrl,
      location: {
        lat,
        lng
      },
      houseRules,
      highlights,
      description,
      amenities
    }
  };
});

