    }
  }
}];

const items = $input.all();

// üëâ T√¨m item ch·ª©a k·∫øt qu·∫£ t√¨m ki·∫øm
const searchItem = items.find(i => i.json?.result?.content?.[0]?.text || i.json?.content?.[0]?.text);
const rawText = searchItem?.json?.result?.content?.[0]?.text || searchItem?.json?.content?.[0]?.text;
const parsed = JSON.parse(rawText || "{}");

const listings = parsed.searchResults || [];
const nextCursor = parsed.paginationInfo?.nextPageCursor || null;

// üëâ L·∫•y loopCount hi·ªán t·∫°i v√† all_listings t·ª´ context
const loopItem = items.find(i => typeof i.json?.loopCount !== "undefined");
const currentLoop = loopItem?.json?.loopCount || 0;
const nextLoop = currentLoop + 1;
const allListings = loopItem?.json?.all_listings || [];

const processedListings = listings.map((item) => {
  const name = item.demandStayListing?.description?.name?.YOUR_AWS_SECRET_KEY_HERE || null;
  const primaryLine = item.structuredContent?.primaryLine || "";
  const totalPrice = item.structuredDisplayPrice?.secondaryLine?.accessibilityLabel || null;
  const pricePerNight = item.structuredDisplayPrice?.primaryLine?.accessibilityLabel || null;
  const priceDetails = item.structuredDisplayPrice?.explanationData?.priceDetails || null;
  const ratingText = item.avgRatingA11yLabel || "";

  const ratingMatch = ratingText.match(/([\d.]+)\s+out\s+of\s+5/);
  const reviewsMatch = ratingText.match(/(\d+)\s*reviews?/);

  const badge = item.badges || null;
  const coordinate = item.demandStayListing?.location?.coordinate || {};
  const checkin = item.listingParamOverrides?.checkin || null;
  const checkout = item.listingParamOverrides?.checkout || null;
  const url = item.url || "";

  const extractBedsRooms = () => {
    if (primaryLine.toLowerCase().includes("bed")) return primaryLine;
    const nameMatchBR = name?.match(/(\d+)\s*BR/i);
    if (nameMatchBR) return `${nameMatchBR[1]} bedroom`;
    if (name && /studio/i.test(name)) return "Studio";
    if (primaryLine && /studio/i.test(primaryLine)) return "Studio";
    return null;
  };

  const idMatch = url.match(/\/rooms\/(\d+)/);
  const id = idMatch ? idMatch[1] : null;

  return {
    id,
    name,
    beds_rooms: extractBedsRooms(),
    price_per_night: pricePerNight,
    total_price: totalPrice,
    price_details: priceDetails,
    rating: ratingMatch ? parseFloat(ratingMatch[1]) : null,
    reviews: reviewsMatch ? parseInt(reviewsMatch[1]) : null,
    location: {
      lat: coordinate.latitude || null,
      lng: coordinate.longitude || null
    },
    badge,
    checkin,
    checkout,
    url,
    page: nextLoop,
    isMeta: false
  };
});

// üëâ G·ªôp listings m·ªõi v√†o m·∫£ng t·ªïng
const updatedAllListings = [...allListings, ...processedListings];

// üëâ T·∫°o 1 item metadata duy nh·∫•t v·ªõi all_listings
const meta = {
  loopCount: nextLoop,
  cursor: nextCursor,
  all_listings: updatedAllListings,
  current_page_count: processedListings.length,
  total_count: updatedAllListings.length,
  isMeta: true
};

// üëâ Ch·ªâ tr·∫£ v·ªÅ metadata
return [{ json: meta }];

