// Step 1: Map the items and add properties
const mappedItems = items.map(item => {
  const query = (item.json.query || "").toLowerCase();
  const page = (item.json.page || "").toLowerCase();

  // Update with your own brand terms"
  const isBrand = query.includes("BRAND TERM 1", "BRAND TERM 2", "ETC.");
  // Remove if you don't want to also segment brand terms by page group, or update with your own group, or add more as needed
  const isRecipes = page.includes("/recipes");

  // Tag segment type ‚Äî this is optional now that isBrand + isRecipes can coexist
  let segment = "nonbrand";
  if (isBrand && isRecipes) {
    segment = "brand+recipes";
  } else if (isBrand) {
    segment = "brand";
  } else if (isRecipes) {
    segment = "recipes";
  }

  return {
    json: {
      ...item.json,
      isBrand,
      isRecipes,
      segment
    }
  };
});

// Step 2: Sort by deltaClicks
const sortedItems = mappedItems.sort((a, b) => {
  const deltaClicksA = a.json.deltaClicks || 0;
  const deltaClicksB = b.json.deltaClicks || 0;

  // Sorting in descending order (highest deltaClicks first)
  return deltaClicksB - deltaClicksA;
});

// Return the sorted items
return sortedItems;

function formatDate(date) {
  return date.toISOString().split('T')[0];
}

const today = new Date();
const prior = new Date(today);
prior.setDate(today.getDate() - 2);
const last = new Date(today);
last.setDate(today.getDate() - 1);

return [
  { json: { label: 'priorDay', startDate: formatDate(prior), endDate: formatDate(prior) } },
  { json: { label: 'lastDay', startDate: formatDate(last), endDate: formatDate(last) } }
];

// Split all items by day
const prior = [];
const last = [];

for (const item of items) {
  if (item.json.day === "priorDay") {
    prior.push(item.json);
  } else if (item.json.day === "lastDay") {
    last.push(item.json);
  }
}

// Index prior day rows by page+query
const priorMap = new Map();
for (const row of prior) {
  const key = `${row.page}|||${row.query}`;
  priorMap.set(key, row);
}

// Match last day rows and compute deltas + formatted % changes
const results = [];

for (const row of last) {
  const key = `${row.page}|||${row.query}`;
  const previous = priorMap.get(key);
  if (!previous) continue;

  const clicksDelta = row.clicks - previous.clicks;
  const ctrDeltaRaw = row.ctr - previous.ctr;
  const impressionsDelta = row.impressions - previous.impressions;
  const positionDelta = row.position - previous.position;

  const percentChangeClicks = previous.clicks !== 0 ? `${((clicksDelta / previous.clicks) * 100).toFixed(1)}%` : null;
  const percentChangeCTR = previous.ctr !== 0 ? `${((ctrDeltaRaw / previous.ctr) * 100).toFixed(1)}%` : null;
  const percentChangeImpressions = previous.impressions !== 0 ? `${((impressionsDelta / previous.impressions) * 100).toFixed(1)}%` : null;
  const percentChangePosition = previous.position !== 0 ? `${((positionDelta / previous.position) * 100).toFixed(1)}%` : null;

  const ctrLastDay = `${(row.ctr * 100).toFixed(1)}%`;
  const ctrPriorDay = `${(previous.ctr * 100).toFixed(1)}%`;
  const deltaCTR = `${(ctrDeltaRaw * 100).toFixed(1)}%`;

  results.push({
    json: {
      page: row.page,
      query: row.query,

      deltaClicks: clicksDelta,
      percentChangeClicks,
      clicksLastDay: row.clicks,
      clicksPriorDay: previous.clicks,

      deltaCTR,
      percentChangeCTR,
      ctrLastDay,
      ctrPriorDay,

      deltaImpressions: impressionsDelta,
      percentChangeImpressions,
      impressionsLastDay: row.impressions,
      impressionsPriorDay: previous.impressions,

      deltaPosition: positionDelta,
      percentChangePosition,
      positionLastDay: row.position,
      positionPriorDay: previous.position
    }
  });
}

return results;
