// Generate discount codes for recovery sequence
const cartId = $json.cart_id;
const customerEmail = $json.customer_email;
const timestamp = Date.now();

// Create unique discount codes
const codes = {
  firstCode: `SAVE${$node['Recovery Settings'].json.firstDiscount}-${cartId.slice(-4)}`,
  secondCode: `SAVE${$node['Recovery Settings'].json.secondDiscount}-${cartId.slice(-4)}`,
  finalCode: `SAVE${$node['Recovery Settings'].json.finalDiscount}-${cartId.slice(-4)}`
};

// Calculate recovery schedule
const schedules = {
  firstEmail: new Date(timestamp + 1 * 60 * 60 * 1000).toISOString(), // 1 hour
  secondEmail: new Date(timestamp + 24 * 60 * 60 * 1000).toISOString(), // 24 hours
  finalEmail: new Date(timestamp + 72 * 60 * 60 * 1000).toISOString()  // 72 hours
};

return {
  ...codes,
  ...schedules,
  cartData: $json,
  recoveryId: `recovery_${cartId}_${timestamp}`
};
