parameters.jsCode:
const emails = $input.all().map((item) => item.json);

const extractedData = emails.map((email) => {
  // Remove line breaks and excessive spaces from body
  const cleanBody = email.text
    ?.replace(/(\r\n|\n|\r)/gm, ' ')  // Replace newlines with space
    ?.replace(/\s+/g, ' ')           // Collapse multiple spaces
    ?.trim();                        // Remove leading/trailing space

  return {
    to: email.to?.value?.[0]?.address || '',
    from: email.from?.value?.[0]?.address || '',
    fromName: email.from?.value?.[0]?.name || '',
    date: email.date || '',
    subject: email.subject || '',
    body: cleanBody || '',
    emailId: email.id || '',
  };
});

return extractedData;

