let organizations = $input.all()[0].json.entities.filter(d => {
  let hasOrg = false;
  let isConfident = false;
  let types = d.allTypes.reduce((names, i) => {
    names.push(i.name);
    return names;
  },[]);
  if(types.indexOf('organization') >= 0) hasOrg = true;
  isConfident = d.confidence >= 0.85;
  return (hasOrg && isConfident);
});

let orgNames = organizations.reduce((names, o) => {
  names.push(o.name);
  return names;
}, []);

return { organizations:orgNames, summary:$input.all()[0].json.summary }

