={{ 
  (() => {
    const email = $json.customer_email || $json['email address '] || '';
    const now = Date.now().toString();

    let suffix = '';
    if (email.length > 0) {
      const last4 = email.slice(-4);
      for(let i=0; i<last4.length; i++){
        suffix += last4.charCodeAt(i);
      }
      suffix = suffix.slice(0,6);
    } else {
      suffix = Math.floor(Math.random() * 900000 + 100000).toString();
    }

    return now + '-' + suffix;
  })()
}}

