// Validate required input parameters
const body = $input.first().json.body || $input.first().json.chatInput;

if (!body) {
  throw new Error('Request body is required');
}

const { url, userPrompt, selectors } = body;

if (!url) {
  throw new Error('URL is required');
}

// Validate URL format using regex
const urlRegex = /^https?:\/\/(www\.)?[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)$/;
if (!urlRegex.test(url)) {
  throw new Error('Invalid URL format. Please provide a valid HTTP or HTTPS URL');
}

// Set default selectors if not provided
const defaultSelectors = {
  title: 'title, h1',
  content: 'p, .content, article',
  links: 'a[href]',
  images: 'img[src]'
};

return {
  json: {
    url: url,
    userPrompt: userPrompt,
    selectors: selectors || defaultSelectors,
    timestamp: new Date().toISOString()
  }
};

// Analyze robots.txt content for scraping permissions
const robotsResponse = $input.first().json;
const originalData = $('validate-input').first().json;

let isAllowed = true;
let robotsInfo = 'No robots.txt found - proceeding with caution';

if (robotsResponse && !robotsResponse.error) {
  const robotsContent = robotsResponse.data || '';
  robotsInfo = 'Robots.txt found and analyzed';

  // Simple robots.txt analysis
  const lines = robotsContent.toLowerCase().split('\n');
  let userAgentSection = false;

  for (const line of lines) {
    const trimmed = line.trim();

    if (trimmed.startsWith('user-agent:')) {
      userAgentSection = trimmed.includes('*') || trimmed.includes('n8n');
    }

    if (userAgentSection && trimmed.startsWith('disallow:')) {
      const path = trimmed.replace('disallow:', '').trim();
      if (path === '/' || (path && originalData.url.includes(path))) {
        isAllowed = false;
        break;
      }
    }
  }
}

if (!isAllowed) {
  throw new Error('Scraping is not allowed according to robots.txt');
}

return {
  json: {
    ...originalData,
    robotsInfo: robotsInfo,
    scrapingAllowed: isAllowed
  }
};

// Loop over input items and stringify the JSON of each one
for (const item of $input.all()) {
  item.json.myStringifiedField = JSON.stringify(item.json.someObject);
}
return $input.all();
