const itemsOut = [];

const labels = [];
const data = [];

for (const item of items) {
  const name = item.json.name;
  let cap = item.json.market_cap;
  if (cap === undefined || cap === null) cap = "0";
  cap = String(cap);

  let value = 0;
  if (cap.includes("T")) value = parseFloat(cap) * 1e12;
  else if (cap.includes("B")) value = parseFloat(cap) * 1e9;
  else if (cap.includes("M")) value = parseFloat(cap) * 1e6;
  else value = parseFloat(cap);

  labels.push(name);
  data.push(Math.round(value / 1e9));
}

const chartPayload = {
  type: 'bar',
  data: {
    labels,
    datasets: [{
      label: 'Market Cap (in Billion $)',
      data,
      backgroundColor: [
        'rgba(75, 192, 192, 0.8)',
        'rgba(153, 102, 255, 0.8)',
        'rgba(255, 159, 64, 0.8)',
        'rgba(255, 99, 132, 0.8)',
        'rgba(54, 162, 235, 0.8)',
        'rgba(201, 203, 207, 0.8)'
      ],
      borderColor: 'rgba(0,0,0,0.1)',
      borderWidth: 1
    }]
  },
  options: {
    indexAxis: 'y',
    responsive: true,
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: true,
        text: 'üìä Company Market Cap Overview',
        font: {
          size: 20
        },
        padding: {
          top: 10,
          bottom: 20
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            return context.dataset.label + ': $' + context.raw + 'B';
          }
        }
      }
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Market Cap (in Billions $)'
        },
        grid: {
          display: true
        }
      },
      y: {
        ticks: {
          font: {
            size: 12
          }
        }
      }
    }
  }
};

itemsOut.push({ json: { chart: chartPayload } });
return itemsOut;
