parameters.jsCode:
const cwv = [
'first-contentful-paint',
'speed-index',
'largest-contentful-paint',
'total-blocking-time',
'interactive',
  'cumulative-layout-shift'
]

const map = new Map();

cwv.map((item) => {
  map.set(item, {
    value: $input.first().json.lighthouseResult.audits[item].displayValue,
    score: $input.first().json.lighthouseResult.audits[item].score
  })
})

const obj = Object.fromEntries(map);

const audits = Object.keys($input.first().json.lighthouseResult.audits).filter((key) => !key.includes(cwv))

let auditsDesc = ""

audits.forEach((key) => {
  auditsDesc += $input.first().json.lighthouseResult.audits[key].title + $input.first().json.lighthouseResult.audits[key].description.replace(/\[[^\]]*\]\([^)]+\)/g, '').replace(/\s{2,}/g, ' ').trim()
})

return [
  {
    json: {
      cwv: obj,
      audits: auditsDesc
    }
  }
]
