let promptEnd = '';
if( $('localVariables').last().json.autoErrorFixing ){
  promptEnd = '. Please fix SQL query. Please decide how to solve and give fixed SQL query. You should give the fixed version of this current SQL query, that can be copied and run straightforward in SQL sandbox';
} else {
  promptEnd = '. Please suggest how we can resolve this error.  At the end of your response, ask: Does it work for us?';
}

return {
  "message"     : $input.last().json.message,
  "description" : $input.last().json.error.description,
  "prompt"      : "Recieved error while executing this query: " + $input.last().json.message + ". Here detailed description: " + $input.last().json.error.description + promptEnd
}

let isAssistantExists = 0;
let assistantId = null;

for (const item of $('getAssistantsList').all()) {
  let trimmedGetAss = item.json.name ? item.json.name.trim() : '';
  let trimmedAgentName = $("AgentName").last().json.agentName.trim();
  if (trimmedGetAss === trimmedAgentName) { 
    isAssistantExists = 1;
    assistantId = item.json.id;
  }
}

return {output: isAssistantExists, id: assistantId};

