const autoErrorFixing = $('localVariables').last().json.autoErrorFixing;
const wasExecuted = $('GenerateErrorPrompt').isExecuted;

if (!autoErrorFixing || !wasExecuted) {
  return [{ json: { details: { errorLoop: [] } } }];
}

const errorLoop = [];

let counter = 0;
do {
  try {
    const erroritems = $items("GenerateErrorPrompt", 0, counter).map(erroritem => { return {errorMessage: erroritem.json.message, description: erroritem.json.error?.description} });
    const outputitems = $items("setOutputByProvider", 0, counter).map(item => { return {output: item.json.output}});
    errorLoop.push([...outputitems, ...erroritems]);
  } catch (error) {
    return [ { json: { details: { errorLoop } } } ];  
  }

  counter++;
} while(true);

