// Parse tracking response and extract relevant information
const items = [];

for (const item of $input.all()) {
  const trackingData = item.json;

  let status = 'unknown';
  let location = '';
  let lastUpdated = '';
  let estimatedDelivery = '';

  // Parse Delhivery response
  if (trackingData.ShipmentData && trackingData.ShipmentData.length > 0) {
    const shipment = trackingData.ShipmentData[0];
    status = shipment.Status?.Status || 'unknown';
    location = shipment.Status?.StatusLocation || '';
    lastUpdated = shipment.Status?.StatusDateTime || '';
    estimatedDelivery = shipment.ExpectedDeliveryDate || '';
  }

  // Parse DHL response
  if (trackingData.shipments && trackingData.shipments.length > 0) {
    const shipment = trackingData.shipments[0];
    if (shipment.status) {
      status = shipment.status.statusCode || 'unknown';
      location = shipment.status.location || '';
      lastUpdated = shipment.status.timestamp || '';
    }
  }

  // Normalize status values
  const normalizedStatus = {
    'Delivered': 'delivered',
    'delivered': 'delivered',
    'In Transit': 'in_transit',
    'in_transit': 'in_transit',
    'Out for Delivery': 'out_for_delivery',
    'Picked up': 'picked_up',
    'Exception': 'exception'
  }[status] || 'in_transit';

  items.push({
    json: {
      tracking_number: item.json.tracking_number || '',
      customer_email: item.json.customer_email || '',
      customer_phone: item.json.customer_phone || '',
      order_id: item.json.order_id || '',
      current_status: normalizedStatus,
      previous_status: item.json.status || '',
      location: location,
      last_updated: lastUpdated,
      estimated_delivery: estimatedDelivery,
      status_changed: (item.json.status !== normalizedStatus)
    }
  });
}

return items;

