// Log workflow execution summary
const processedItems = $input.all().length;
const updatedItems = $input.all().filter(item => item.json.status_changed).length;

console.log(`Shipment Tracking Summary:`);
console.log(`- Total shipments processed: ${processedItems}`);
console.log(`- Shipments with status updates: ${updatedItems}`);
console.log(`- Execution completed at: ${new Date().toISOString()}`);

return [{
  json: {
    summary: {
      processed: processedItems,
      updated: updatedItems,
      timestamp: new Date().toISOString()
    }
  }
}];
