if (items.length === 0) {
  throw new Error("No fresh posts available to pick from.");
}

const randomIndex = Math.floor(Math.random() * items.length);
return [items[randomIndex]];

// Separate merged items into RSS posts and Sheet URLs
const rssItems = [];
const usedUrls = new Set();

for (const item of items) {
  const link = item.json.link;
  const url = item.json.url;

  if (link && !rssItems.find(i => i.json.link === link)) {
    // This is from RSS
    rssItems.push(item);
  } else if (url) {
    // This is from Sheet
    usedUrls.add(url);
  }
}

// Filter out RSS posts whose link already exists in the sheet
const freshPosts = rssItems.filter(item => !usedUrls.has(item.json.link));

return freshPosts;

const articleText = $json.text;
const driveImageURL = $json.drive_url || 'Image URL not found';
const date = new Date().toLocaleString('en-US', { timeZone: 'Africa/Lagos' });
const creator = '@OBISDEV';

// Extract title from the first **bolded** line
const titleMatch = articleText.match(/\*\*(.*?)\*\*/);
const title = titleMatch ? titleMatch[1] : 'Untitled Article';

// Prepare final formatted text
const formattedArticle = 
`${title}\n\n` +
`[IMAGE ATTACHED BELOW]\n${driveImageURL}\n\n` +
`[DATE]\n${date}\n\n` +
`[CREATOR]\nWritten by ${creator}\n\n` +
`[ARTICLE]\n${articleText}\n\n` +
`[FOLLOW]\nStay updated with more insights and tools from Obisdev:\n` +
`X: @OBISDEV\nInstagram: @OBISDEV\nGumroad: @OBISDEV NWUDE`;

return [
  {
    json: {
      title,
      formattedArticle,
      driveImageURL
    }
  }
];

// Filter out the item with the doc ID and the one with the article
let idItem = items.find(item => item.json.id);
let articleItem = items.find(item => item.json.formattedArticle);

// Output a single object with id, formatted article, and drive image URL
return [
  {
    json: {
      id: idItem.json.id,
      formattedArticle: articleItem.json.formattedArticle,
      driveImageURL: articleItem.json.driveImageURL
    }
  }
];

// Safely find input parts
const docId = items.find(item => item.json.id)?.json.id;
const article = items.find(item => item.json.formattedArticle)?.json.formattedArticle || '';

// Try to get title from a dedicated field, fallback to bolded headline
let title = items.find(item => item.json.title)?.json.title;
if (!title) {
  const match = article.match(/\*\*(.+?)\*\*/);
  title = match ? match[1] : 'Untitled Article';
}

// Remove bolded title if it appears again inside the content
const cleanedArticle = article.replace(new RegExp(`\\*\\*${title}\\*\\*`, 'i'), '');

// Remove all structural [TAGS] except [Link: ...]
const mainContent = cleanedArticle
  .replace(/\[(?!Link:).*?\]/g, '')
  .trim();

// Compose final content
const content = `${title}

[Image will be inserted here]

Published: ${new Date().toLocaleString('en-US', { timeZone: 'Africa/Lagos' })}
By: @OBISDEV

${mainContent}
`;

return [
  {
    json: {
      id: docId,
      formattedArticle: content
    }
  }
];

const width = 1024;
const height = 800;
const randomSeed = Math.floor(Math.random() * 100000);

const finalPrompt = $json.text;
const imageUrl = `https://image.pollinations.ai/prompt/${encodeURIComponent(finalPrompt)}.jpg?width=${width}&height=${height}&seed=${randomSeed}&model=flux&nologo=true`;

return [
  {
    json: {
      text: finalPrompt,
      imageUrl
    }
  }
];
