const commitMessage = $json.body.commits[0].message;
const fullRef = $json.body.ref;
const pushBranch = fullRef.startsWith('refs/heads/') ? fullRef.slice('refs/heads/'.length) : fullRef;

// Updated regex to make the [...] part optional
const regex = /^([A-Z]+-\d+)\s(.*?)(?:\s\[(.*)\])?$/;
const match = commitMessage.match(regex);

if (!match) {
  throw new Error("Commit message format is incorrect. Missing task key or message.");
}

const jiraKey = match[1];
const commitDescription = match[2].trim();
const flagString = match[3];

const flags = flagString ? flagString.split(',').map(s => s.trim()) : [];

const baseBranch = flags.find(f => !['auto-pr', 'taskcompleted'].includes(f));
const autoPR = flags.includes('auto-pr');
const taskCompleted = flags.includes('taskcompleted');

// Validation rules
if (autoPR && !baseBranch) {
  throw new Error("Commit message error: Please enter a base branch to create the PR in.");
}

return {
  json: {
    jiraKey,
    commitDescription,
    commands: flags,
    baseBranch,
    autoPR,
    taskCompleted,
    pushBranch
  }
};

