// Input: one item with ocrResponse at items[0].json
const out = [];
const res = items[0].json;

for (const page of res.pages || []) {
  const md = page.markdown || "";
  const imgs = page.images || [];
  // Build a quick lookup by id: "img-0.jpeg" -> image object
  const byId = Object.fromEntries(imgs.map(im => [im.id, im]));
  // Find placeholders in markdown
  const re = /!\[(.*?)\]\((img-\d+\.(?:jpe?g|png|webp|avif))\)/g;
  let m;
  while ((m = re.exec(md)) !== null) {
    const id = m[2];
    const im = byId[id];
    if (!im || !im.image_base64) continue;
    out.push({
      json: {
        pageIndex: page.index,
        imageId: id,
        // This is already a full data URI like "data:image/jpeg;base64,..."
        imageDataUri: im.image_base64
      }
    });
  }
}
return out.length ? out : [{ json: { note: "no-embedded-images" } }];

// n8n Code node: merge main page markdown with OCR text for each img-N / imageN
const rows = $input.all().map(i => i.json);

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ config ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const MERGE_MODE = 'replace'; // 'replace' or 'append'
const EXTS = '(?:jpeg|jpg|png|gif|webp|avif)';

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ helpers ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const asString = v => (v == null ? '' : String(v));
const isStringArray = a => Array.isArray(a) && a.every(x => typeof x === 'string');

// Strip any image tokens from OCR text so we do not re-insert placeholders
const stripImageTokens = s =>
  asString(s)
    .replace(/!\[[^\]]*]\([^)]+\)/gi, '')          // remove markdown image tags
    .replace(/\b(?:img[-_]?|image[-_]?)\d+\.(?:jpeg|jpg|png|gif|webp|avif)\b/gi, '')
    .trim();

// Try to locate the "main" document row (has page-level markdown)
const main =
  rows.find(r => typeof r['pages[0].markdown'] === 'string' && r['pages[0].markdown'].length) ||
  rows.find(r => Array.isArray(r.pages) && r.pages.length && typeof r.pages[0]?.markdown === 'string') ||
  rows.find(r => typeof r.documentMarkdown === 'string' && r.documentMarkdown.length);

if (!main) {
  return [{ json: { error: 'No main document row with pages[].markdown or pages[0].markdown found', rows } }];
}

// Build the starting document markdown
let doc = main['pages[0].markdown'] ||
          (Array.isArray(main.pages) ? main.pages.map(p => p?.markdown || '').join('\n\n') : '') ||
          main.documentMarkdown ||
          '';

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ build idx ‚Üí text map ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const idxToText = new Map();

// 1) Per-image rows: derive index from imageId like "img-12.jpg" or "image12.jpg"
for (const r of rows) {
  if (r === main) continue;

  let imageId = r.imageId || (Array.isArray(r.images) && r.images[0]?.id) || '';
  imageId = asString(imageId);

  // Accept both "img-12.jpg" and "image12.jpg"
  let m = imageId.match(/(?:img[-_]?|image[-_]?)(\d+)\./i);
  if (!m) continue;
  const idx = Number(m[1]);

  // Prefer r.markdown[0], then r['markdown[0]'], then r.imagePages[0].markdown, then r.markdown (string)
  let text = null;
  if (Array.isArray(r.markdown) && r.markdown.length) text = r.markdown[0];
  else if (typeof r['markdown[0]'] === 'string') text = r['markdown[0]'];
  else if (Array.isArray(r.imagePages) && r.imagePages.length) text = r.imagePages[0]?.markdown || '';
  else if (typeof r.markdown === 'string') text = r.markdown;

  if (text != null && !idxToText.has(idx)) {
    idxToText.set(idx, stripImageTokens(text));
  }
}

// 2) Aggregated array case: r['imagePages[0].markdown'] is an array OR r.markdown is an array (your screenshot)
for (const r of rows) {
  const agg =
    (Array.isArray(r['imagePages[0].markdown']) && r['imagePages[0].markdown']) ||
    (isStringArray(r.markdown) && r.markdown);

  if (!agg) continue;

  agg.forEach((t, i) => {
    if (t == null) return;
    if (!idxToText.has(i)) idxToText.set(i, stripImageTokens(t));
  });
}

// If we still have no mapping, bail with debug
if (idxToText.size === 0) {
  return [{
    json: {
      ...main,
      error: 'No OCR mapping built. Neither per-image rows nor aggregated markdown[] found.',
      debug_keys: rows.map(r => Object.keys(r)),
      sample_row: rows[0],
    }
  }];
}

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ replace or append ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
let placeholdersFound = 0;
let placeholdersReplaced = 0;
const missing = [];

const sorted = [...idxToText.entries()].sort((a, b) => a[0] - b[0]);
for (const [idx, textRaw] of sorted) {
  const text = asString(textRaw).trim();
  const nameGroup = `(?:img[-_]?${idx}|image[-_]?${idx})`;

  const patterns = [
    // markdown image: ![alt](img-0.jpg) or ![alt](image0.jpg)
    new RegExp(`!\\[[^\\]]*\\]\\(\\s*${nameGroup}\\.${EXTS}\\s*\\)`, 'gi'),
    // link style: [img-0.jpg](img-0.jpg)
    new RegExp(`\\[\\s*${nameGroup}\\.${EXTS}\\s*\\]\\(\\s*${nameGroup}\\.${EXTS}\\s*\\)`, 'gi'),
    // bare token somewhere in text
    new RegExp(`\\b${nameGroup}\\.${EXTS}\\b`, 'gi'),
  ];

  let localFound = 0;
  for (const rx of patterns) {
    const matches = doc.match(rx);
    if (!matches) continue;
    localFound += matches.length;

    if (MERGE_MODE === 'replace') {
      doc = doc.replace(rx, () => text);
    } else {
      // append under the image/link
      doc = doc.replace(rx, (m) => `${m}\n\n${text}\n`);
    }
  }

  placeholdersFound += localFound;
  placeholdersReplaced += localFound;
  if (localFound === 0) missing.push({ idx, note: 'no placeholder found for this index' });
}

// ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ output ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
return [{
  json: {
    ...main,
    markdown: doc,
    enrichment_debug: {
      placeholdersFound,
      placeholdersReplaced,
      missing,
      mappedCount: idxToText.size,
    },
  },
}];
