const text = $input.first().json.organic[0].snippet;
const regex = /(https?:\/\/(?:www\.|(?!www))[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|www\.[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|https?:\/\/[a-zA-Z0-9]\.[^\s]{2,})(?<![\\.\\,])/g;
const matches = text.match(regex) || [];

return matches.map(url => ({
  json: {
    url,
    link: $input.first().json.organic[0].link
  }
}));
