// Code created by ca7ai
// n8n Code node (Run Once for Each Item)
// Normalizes Security Hub / AWS Config events whether they arrive at root or under body
const evt = $json.body ?? $json;

// Security Hub finding (EventBridge or raw)
const sh = evt?.detail?.findings?.[0] || (Array.isArray(evt.findings) ? evt.findings[0] : null);
// AWS Config notification
const cfg = evt?.detail?.configRuleName ? evt.detail : null;
// Raw Security Hub finding at root
const rawSh = (!sh && evt?.ProductArn) ? evt : null;

const f = sh || rawSh || {};

const sev   = f?.Severity?.Label || (cfg ? "MEDIUM" : "UNKNOWN");
const title = f?.Title || cfg?.configRuleName || "Finding";
const desc  = f?.Description || cfg?.newEvaluationResult?.annotation || "‚Äî";
const id    = f?.Id
  || cfg?.newEvaluationResult?.evaluationResultIdentifier?.evaluationResultQualifier?.configRuleName
  || String(Date.now());
const res   = f?.Resources?.[0]?.Id || cfg?.resourceId || "unknown";
const types = f?.Types || [];
const account = evt?.account || f?.AwsAccountId || "unknown";
const region  = evt?.region  || f?.Region       || "unknown";

// Derive service + hints
let service = "UNKNOWN";
if (/^arn:aws:s3:::/.test(res) || types.some(t => t.includes("S3"))) service = "S3";
else if (types.some(t => /SecurityGroup/i.test(t)) || /sg-/.test(res)) service = "EC2-SG";
else if (types.some(t => /IAM/i.test(t))) service = "IAM";
else if (types.some(t => /RDS|SQL|DB/i.test(t))) service = "RDS";
else if (f?.ProductArn) service = (f.ProductArn.split(":")[5] || "UNKNOWN");

const misconfig_hints = [];
if (service === "S3") misconfig_hints.push("s3");
if (/0\.0\.0\.0\/0|Public|Open|world/i.test(desc)) misconfig_hints.push("public");
if (service === "EC2-SG") misconfig_hints.push("sg");
if (service === "IAM")    misconfig_hints.push("iam");
if (service === "RDS")    misconfig_hints.push("db");

// IMPORTANT: return a SINGLE object (not an array) in this mode
return {
  finding_id: id,
  title,
  description: desc,
  severity: sev,
  resource_id: res,
  service,
  account,
  region,
  product_types: types,
  misconfig_hints,
  raw: evt,
};

