const b = $json.body ?? $json;
const token = $json.query?.token ?? b.token;
if (token !== 'MY_SUPER_TOKEN') throw new Error('unauthorized');

if (b.Type === 'SubscriptionConfirmation' && b.SubscribeURL) {
  return { mode: 'confirm', subscribeUrl: b.SubscribeURL };
}

let event = b;
if (b.Type === 'Notification' && b.Message) {
  try { event = JSON.parse(b.Message); } catch {}
}
return { mode: 'notify', event };
