        const dataArray = Array.isArray(parsedData) ? parsedData : [parsedData];

        // ÂêÑ„Ç¢„Ç§„ÉÜ„É†„ÇíÂá¶ÁêÜ
        dataArray.forEach((item) => {
          if (item && typeof item === 'object') {
            const dishName = item.dishName || item.dish_name || item.name || 'Unknown';
            let calories = item.calories || item.calorie || item.cal || 0;

            // „Ç´„É≠„É™„Éº„ÅåÊñáÂ≠óÂàó„ÅÆÂ†¥Âêà„ÅØÊï∞ÂÄ§„Å´Â§âÊèõ
