                         text.match(/"calorie"\s*:\s*"?(\d+)"?/i) ||
                         text.match(/"cal"\s*:\s*"?(\d+)"?/i);

    if (dishMatch && caloriesMatch) {
      const calories = parseInt(caloriesMatch[1]);
      if (!isNaN(calories) && calories > 0) {
