// n8n Code node (Run once for all items)
//
// Purpose
// -------
// Merge the four inputs coming from the previous Merge node into a single,
// clean JSON ready for Odoo `crm.lead` create.
//
// Expected inputs (from Merge):
// - 1 item from the webhook: { body: { firstname, lastname, email, phone, notes, ... } }
//   (or, in some setups, those fields may be at the root instead of under `body`)
// - 1 item with { source_id }
// - 1 item with { medium_id }
// - 1 item with { campaign_id }
//
// Output:
// - 1 item: { name, contact_name, email_from, phone, description, type, campaign_id, source_id, medium_id }

function s(x) { return (x ?? '').toString().trim(); }

let payload = null;
let source_id = null;
let medium_id = null;
let campaign_id = null;

// Scan all items coming from Merge and collect what we need
for (const it of items) {
  const j = it.json ?? it;

  // Identify the webhook item.
  // Prefer `j.body` (typical webhook shape), but fall back to root-level fields just in case.
  if (j.body || j.firstname || j.lastname || j.email || j.notes || j.phone) {
    payload = j.body ?? j;
  }

  // Collect UTM IDs (take the first defined value seen for each)
  if (j.source_id !== undefined && j.source_id !== null && source_id === null) {
    const n = Number(j.source_id);
    source_id = Number.isFinite(n) ? n : null;
  }
  if (j.medium_id !== undefined && j.medium_id !== null && medium_id === null) {
    const n = Number(j.medium_id);
    medium_id = Number.isFinite(n) ? n : null;
  }
  if (j.campaign_id !== undefined && j.campaign_id !== null && campaign_id === null) {
    const n = Number(j.campaign_id);
    campaign_id = Number.isFinite(n) ? n : null;
  }
}

// Safety check: if the webhook item is missing, fail early with a helpful message
if (!payload) {
  throw new Error('Webhook payload not found after Merge. Ensure the IF(false) branch is connected to the Merge.');
}

// Build the final lead payload for Odoo
const first = s(payload.firstname);
const last  = s(payload.lastname);
const fullName = `${first} ${last}`.trim();

const out = {
  // Core lead fields
  name: fullName || s(payload.email) || 'New Lead',
  contact_name: fullName,
  email_from: s(payload.email),
  phone: s(payload.phone),
  description: s(payload.notes),
  type: 'lead',

  // UTM Many2one IDs (nullable if not provided/found)
  campaign_id,
  source_id,
  medium_id,
};

return [{ json: out }];
