// Create simple reminder schedule
const items = $input.all();

return items.map(item => {
  const data = item.json;

  // Parse frequency (default 3 times daily)
  const timesPerDay = parseInt(data.times_per_day) || 3;
  const durationDays = parseInt(data.duration_days) || 7;

  // Standard time slots
  const timeSlots = {
    1: ['09:00'],
    2: ['09:00', '21:00'], 
    3: ['08:00', '14:00', '20:00'],
    4: ['08:00', '12:00', '16:00', '20:00']
  };

  const slots = timeSlots[timesPerDay] || timeSlots[3];
  const startDate = new Date(data.start_date || new Date());

  // Create reminders for each day and time
  const reminders = [];
  for (let day = 0; day < durationDays; day++) {
    slots.forEach(time => {
      const reminderDate = new Date(startDate);
      reminderDate.setDate(startDate.getDate() + day);
      const [hours, minutes] = time.split(':');
      reminderDate.setHours(hours, minutes, 0, 0);

      if (reminderDate > new Date()) {
        reminders.push({
          prescription_id: data.prescription_id,
          patient_name: data.patient_name,
          patient_phone: data.patient_phone,
          patient_email: data.patient_email,
          medication: data.medication,
          dosage: data.dosage,
          reminder_time: reminderDate.toISOString(),
          day_number: day + 1,
          time_slot: time,
          status: 'scheduled'
        });
      }
    });
  }

  return {
    json: {
      ...data,
      reminders: reminders,
      total_reminders: reminders.length
    }
  };
});

// Find reminders due now
const items = $input.all();
const now = new Date();
const bufferMinutes = 10;

const dueReminders = items.filter(item => {
  const data = item.json;

  if (data.status !== 'scheduled') return false;

  const reminderTime = new Date(data.reminder_time);
  const timeDiff = (now - reminderTime) / (1000 * 60);

  return timeDiff >= 0 && timeDiff <= bufferMinutes;
});

return dueReminders.map(reminder => ({
  json: {
    ...reminder.json,
    message: `üíä Medication Reminder\n\nHi ${reminder.json.patient_name}!\n\nTime for: ${reminder.json.medication}\nDosage: ${reminder.json.dosage}\nTime: ${reminder.json.time_slot}\nDay ${reminder.json.day_number}\n\nReply 'TAKEN' to confirm.`
  }
}));
