// On récupère le tableau de projets depuis la réponse de la requête HTTP
const responseData = items[0].json;

// Vérifie si la structure attendue existe
if (!responseData || !responseData.projects || !Array.isArray(responseData.projects)) {
  // Retourne un tableau vide si la structure n'est pas celle attendue
  return [];
}

// Crée un nouvel élément pour chaque projet
const newItems = responseData.projects.map(project => {
  return {
    json: {
      // On copie toutes les propriétés du projet
      ...project,
      // Ajout optionnel de métadonnées de la réponse originale si nécessaire
      _meta: responseData.meta
    }
  };
});

// Retourne le tableau des nouveaux éléments
return newItems;
