// NODE 2: Validate Models Against Dictionaries

const item = $input.first().json;
const models_used = item.models_used || [];
const standardize_names_dic = item.standardize_names_dic || {};
const model_price_dic = item.model_price_dic || {};

// Check which models are missing from each dictionary
const missing_from_names = models_used.filter(model => !standardize_names_dic[model]);
const missing_from_prices = models_used.filter(model => !model_price_dic[model]);

// Build result
if (missing_from_names.length === 0 && missing_from_prices.length === 0) {
  return [{
    json: {
      passed: true,
      message: "All models validated successfully",
      models_used: models_used,
      standardize_names_dic: standardize_names_dic,
      model_price_dic: model_price_dic,
      original_data: item.original_data
    }
  }];
} else {
  const errors = [];
  if (missing_from_names.length > 0) {
    errors.push(`Missing from standardize_names_dic: ${missing_from_names.join(', ')}`);
  }
  if (missing_from_prices.length > 0) {
    errors.push(`Missing from model_price_dic: ${missing_from_prices.join(', ')}`);
  }

  return [{
    json: {
      passed: false,
      message: errors.join(' | '),
      missing_from_names: missing_from_names,
      missing_from_prices: missing_from_prices,
      models_used: models_used
    }
  }];
}

