const data = $input.first().json;
const newFileId = data.newFileId;
const originalPermissions = JSON.parse(data.originalPermissions || '[]');
const permissionsToApply = [];
if (originalPermissions.length > 0) {
  for (const permission of originalPermissions) {
    if (permission.role !== 'owner' && permission.type && permission.role) {
      const permObj = {
        fileId: newFileId,
        type: permission.type,
        role: permission.role
      };
      if (permission.type === 'user' || permission.type === 'group') {
        if (permission.emailAddress) {
          permObj.emailAddress = permission.emailAddress;
        }
      } else if (permission.type === 'domain') {
        if (permission.domain) {
          permObj.domain = permission.domain;
        }
      }
      permissionsToApply.push(permObj);
    }
  }
}
if (permissionsToApply.length > 0) {
  return permissionsToApply.map(p => ({ json: p }));
} else {
  return [];
}

