            children: parseInlineElements(afterHeading)
          });
        }
      } else {
        // Regular content - add as paragraph
        blocks.push({
          type: 'paragraph',
          children: parseInlineElements(remaining)
        });
      }
    }

    return blocks;
  }

  // Extract list items
  function extractListItems(listContent) {
    const items = [];
