// n8n Code node (Function) ‚Äî POML ‚Üí Prompt/Messages (zero-deps, schema-driven)
//
// Supported tags (subset):
// <poml|text|p>, <h level=N>, <b>/<strong>, <i>/<em>,
// <list list-style=...> <item>, <code inline|lang|blank-line>,
// <img alt|src|base64|type>, <audio alt|src>, <br newLineCount=N>,
// <table records|columns>, <Conversation>, <SystemMessage>/<HumanMessage>/<AiMessage>,
// <system-msg>/<user-msg>/<ai-msg>
// Unknown tags render their children.
//
// How to pass your JSON specs:
//  - Set node A ‚Üí set "componentSpec" = (your components JSON object)
//  - Set node B ‚Üí set "attributeSpec" = (your attributes JSON object or array)
// This node will auto-detect common shapes.

function get(obj, path, fallback='') {
  if (!path) return fallback;
  const segs = String(path).split('.').map(s => s.trim()).filter(Boolean);
  let cur = obj;
  for (const s of segs) {
    if (cur && Object.prototype.hasOwnProperty.call(cur, s)) cur = cur[s];
    else return fallback;
  }
  return (cur == null ? fallback : cur);
}

function substitute(str, ctx) {
  if (!str) return '';
  return String(str).replace(/\{\{\s*([a-zA-Z0-9_.$[\]-]+)\s*\}\}/g, (_, p1) => {
    const v = get(ctx, p1, '');
    return (v == null ? '' : String(v));
  });
}

// Minimal XML-ish tokenizer ‚Üí AST
function parsePoml(input) {
  const src = String(input);
  const root = { type: 'element', name: 'root', attrs: {}, children: [] };
  const stack = [root];
  let i = 0;
  const attrRe = /([:@a-zA-Z_][\w:.-]*)\s*=\s*("([^"]*)"|'([^']*)'|([^\s"'>/]+))/g;

  while (i < src.length) {
    const lt = src.indexOf('<', i);
    if (lt < 0) { const text = src.slice(i); if (text) stack[stack.length-1].children.push({ type:'text', text }); break; }
    if (lt > i) { const text = src.slice(i, lt); if (text) stack[stack.length-1].children.push({ type:'text', text }); i = lt; }

    if (src.startsWith('<!--', i)) { const end = src.indexOf('-->', i+4); i = (end >= 0 ? end+3 : src.length); continue; }

    const closeMatch = src.slice(i).match(/^<\/\s*([a-zA-Z_][\w:.-]*)\s*>/);
    if (closeMatch) {
      const tag = closeMatch[1].toLowerCase();
      for (let j = stack.length - 1; j >= 0; j--) {
        if (stack[j].name.toLowerCase() === tag) { stack.length = j; break; }
      }
      i += closeMatch[0].length;
      continue;
    }

    const openMatch = src.slice(i).match(/^<\s*([a-zA-Z_][\w:.-]*)([^>]*)>/);
    if (openMatch) {
      const tag = openMatch[1];
      let rest = openMatch[2] || '';
      const selfClosing = /\/\s*$/.test(rest);
      if (selfClosing) rest = rest.replace(/\/\s*$/, '');
      const attrs = {};
      attrRe.lastIndex = 0;
      let m;
      while ((m = attrRe.exec(rest))) {
        const key = m[1];
        const val = m[3] ?? m[4] ?? m[5] ?? '';
        attrs[key] = val;
      }
      const el = { type:'element', name: tag, attrs, children: [] };
      stack[stack.length-1].children.push(el);
      i += openMatch[0].length;
      if (!selfClosing) stack.push(el);
      continue;
    }

    // literal '<'
    stack[stack.length-1].children.push({ type:'text', text:'<' });
    i += 1;
  }
  return root.children;
}

// Spec normalization ‚Äî accepts multiple shapes
function normalizeComponentSpec(spec) {
  const map = {};
  if (!spec) return map;
  // shape A: { components: { tag: [attrs...] } }
  if (spec.components && typeof spec.components === 'object') {
    for (const [k, v] of Object.entries(spec.components)) map[k.toLowerCase()] = new Set((v||[]).map(a => String(a).toLowerCase()));
    return map;
  }
  // shape B: { tag: [attrs...] }
  for (const [k, v] of Object.entries(spec)) {
    if (Array.isArray(v)) map[k.toLowerCase()] = new Set(v.map(a => String(a).toLowerCase()));
  }
  return map;
}

function normalizeAttributeSpec(spec) {
  // return { attrNameLower: { type: 'boolean'|'integer'|'string'|..., ... } }
  const out = {};
  if (!spec) return out;
  if (Array.isArray(spec)) {
    for (const a of spec) {
      const name = (a && (a.attribute || a.name || a.key)) ? String(a.attribute || a.name || a.key).toLowerCase() : null;
      if (name) out[name] = a;
    }
    return out;
  }
  if (typeof spec === 'object') {
    for (const [k, v] of Object.entries(spec)) out[String(k).toLowerCase()] = (typeof v === 'object' ? v : { type: String(v) });
  }
  return out;
}

// hyphen/camel resolver with type coercion
function readAttr(attrs, name, attrSpecItem, fallback) {
  const keys = [name, name.replace(/-([a-z])/g, (_,c)=>c.toUpperCase()), name.replace(/[A-Z]/g, c=>'-'+c.toLowerCase())];
  let raw; for (const k of keys) if (attrs[k] !== undefined) { raw = attrs[k]; break; }
  if (raw === undefined) return fallback;

  const t = (attrSpecItem && attrSpecItem.type || '').toLowerCase();
  if (t === 'boolean') return /^(true|1|yes)$/i.test(String(raw));
  if (t === 'integer' || t === 'number') { const n = Number.parseInt(raw,10); return Number.isFinite(n) ? n : fallback; }
  if (t === 'ai/human/system') return String(raw).toLowerCase();
  // object/array as JSON
  if (t === 'object' || t === 'array') {
    if (typeof raw === 'string') { try { return JSON.parse(raw); } catch { return fallback; } }
    return raw;
  }
  return raw; // string / enum / unknown
}

const md = {
  fence(code, lang='', addBlank=false){return `${addBlank?'\n':''}\`\`\`${lang}\n${String(code).replace(/\s+$/,'')}\n\`\`\`${addBlank?'\n':''}`;},
};

const trimLines = (s) => String(s).replace(/[ \t]+\n/g, '\n');

function compileNodes(nodes, ctx, roleCtx, options) {
  let out = '';
  for (const n of nodes) out += compileNode(n, ctx, roleCtx, options);
  return out;
}

function compileNode(node, ctx, roleCtx, options) {
  if (node.type === 'text') return substitute(node.text, ctx);

  const name = node.name.toLowerCase();
  const attrs = node.attrs || {};
  const attrSpec = options.attrSpec || {};
  const componentMap = options.componentMap || {};

  // (Optional) component attribute whitelist (non-fatal)
  const allow = componentMap[name];
  if (allow) {
    for (const k of Object.keys(attrs)) {
      if (!allow.has(String(k).toLowerCase())) {
        // drop unknown attrs silently (or collect warnings in options.warnings)
        // delete attrs[k];
      }
    }
  }

  // speaker override
  const speakerAttr = (readAttr(attrs, 'speaker', attrSpec['speaker'] || { type:'ai/human/system' }, '') || '').toLowerCase();
  let nextRoleCtx = roleCtx;
  if (speakerAttr === 'system') nextRoleCtx = 'system';
  if (speakerAttr === 'human')  nextRoleCtx = 'user';
  if (speakerAttr === 'ai')     nextRoleCtx = 'assistant';

  switch (name) {
    case 'poml':
    case 'text':
    case 'p':
      return trimLines(compileNodes(node.children, ctx, nextRoleCtx, options));

    case 'h':
    case 'header': {
      let level = readAttr(attrs, 'level', attrSpec['level'] || { type:'integer' }, 1);
      level = Math.min(6, Math.max(1, level || 1));
      const inner = trimLines(compileNodes(node.children, ctx, nextRoleCtx, options)).trim();
      return `\n\n${'#'.repeat(level)} ${inner}\n\n`;
    }

    case 'b':
    case 'bold':
    case 'strong': {
      const inner = compileNodes(node.children, ctx, nextRoleCtx, options).trim();
      return `**${inner}**`;
    }

    case 'i':
    case 'italic':
    case 'em': {
      const inner = compileNodes(node.children, ctx, nextRoleCtx, options).trim();
      return `_${inner}_`;
    }

    case 'list':
    case 'ul': {
      const style = readAttr(attrs, 'list-style', attrSpec['list-style'] || { type:'string' }, options.listStyle || 'dash')
                 || readAttr(attrs, 'listStyle', attrSpec['list-style'] || { type:'string' }, options.listStyle || 'dash');
      const bullet = style==='decimal' ? '1.' : style==='star' ? '*' : style==='plus' ? '+' : style==='latin' ? 'a.' : '-';
      const lines = [];
      for (const ch of node.children) {
        if (ch.type === 'element' && (ch.name.toLowerCase() === 'item' || ch.name.toLowerCase() === 'li')) {
          const t = trimLines(compileNodes(ch.children, ctx, nextRoleCtx, options)).replace(/\n+/g, ' ').trim();
          if (t) lines.push(`${bullet} ${t}`);
        }
      }
      return `\n${lines.join('\n')}\n`;
    }

    case 'item':
    case 'li': {
      const t = trimLines(compileNodes(node.children, ctx, nextRoleCtx, options)).replace(/\n+/g, ' ').trim();
      return `- ${t}\n`;
    }

    case 'code': {
      const inline = readAttr(attrs, 'inline', attrSpec['inline'] || { type:'boolean' }, true);
      const lang   = readAttr(attrs, 'lang',   attrSpec['lang']   || { type:'string' }, '');
      const blankA = readAttr(attrs, 'blank-line', attrSpec['blank-line'] || { type:'boolean' }, false);
      const blankB = readAttr(attrs, 'blankLine',  attrSpec['blank-line'] || { type:'boolean' }, false);
      const addBlank = Boolean(blankA || blankB);
      const body = compileNodes(node.children, ctx, nextRoleCtx, options);
      return inline ? '`' + body.replace(/`/g, '\\`') + '`' : md.fence(body, lang, addBlank);
    }

    case 'img':
    case 'image': {
      const alt  = readAttr(attrs, 'alt',  attrSpec['alt']  || { type:'string' }, '');
      let   src  = readAttr(attrs, 'src',  attrSpec['src']  || { type:'string' }, '');
      const b64  = readAttr(attrs, 'base64', attrSpec['base64'] || { type:'string' }, '');
      const mime = readAttr(attrs, 'type', attrSpec['type'] || { type:'string' }, 'application/octet-stream');
      if (!src && b64) src = `data:${mime};base64,${b64}`;
      return src ? `\n\n![${alt||''}](${src})\n\n` : (alt ? `\n\n${alt}\n\n` : '');
    }

    case 'audio': {
      const alt = readAttr(attrs, 'alt', attrSpec['alt'] || { type:'string' }, '');
      const src = readAttr(attrs, 'src', attrSpec['src'] || { type:'string' }, '');
      return `\n\n[Audio: ${alt || src || 'audio'}]\n\n`;
    }

    case 'br': {
      const n = Math.max(1, readAttr(attrs, 'newLineCount', attrSpec['newLineCount'] || { type:'integer' }, 1));
      return '\n'.repeat(n);
    }

    case 'table': {
      let records = readAttr(attrs, 'records', attrSpec['records'] || { type:'object' }, null);
      if (typeof records === 'string') { try { records = JSON.parse(records); } catch {} }
      const columns = readAttr(attrs, 'columns', attrSpec['columns'] || { type:'object' }, null);
      if (!Array.isArray(records) || records.length === 0) return '';
      let headers;
      if (Array.isArray(columns) && columns.length) headers = columns.map(String);
      else if (typeof records[0] === 'object' && !Array.isArray(records[0])) headers = Object.keys(records[0]);
      else headers = records[0].map((_,i)=>`col${i+1}`);
      const rows = records.map(r => Array.isArray(r) ? r : headers.map(h => r[h]));
      const head = `| ${headers.join(' | ')} |`;
      const sep  = `| ${headers.map(()=> '---').join(' | ')} |`;
      const body = rows.map(row => `| ${row.map(v => String(v ?? '')).join(' | ')} |`).join('\n');
      return `\n${head}\n${sep}\n${body}\n`;
    }

    // Message components (speakerMode aware)
    case 'systemmessage':
    case 'humanmessage':
    case 'aimessage': {
      const role = name === 'systemmessage' ? 'system' : name === 'humanmessage' ? 'user' : 'assistant';
      const txt  = trimLines(compileNodes(node.children, ctx, role, options)).trim();
      if (options.speakerMode) { options.messages.push({ role, content: txt }); return ''; }
      return `\n\n**${role.toUpperCase()}:**\n${txt}\n`;
    }

    case 'system-msg':
    case 'user-msg':
    case 'ai-msg': {
      const role = name === 'system-msg' ? 'system' : name === 'user-msg' ? 'user' : 'assistant';
      const txt  = trimLines(compileNodes(node.children, ctx, role, options)).trim();
      if (options.speakerMode) { options.messages.push({ role, content: txt }); return ''; }
      return `\n\n**${role.toUpperCase()}:**\n${txt}\n`;
    }

    case 'conversation':
      return trimLines(compileNodes(node.children, ctx, nextRoleCtx, options));

    default:
      // Unknown tag ‚Üí render children
      return trimLines(compileNodes(node.children, ctx, nextRoleCtx, options));
  }
}

// ---- main per-item loop
const items = $input.all();
const out = [];

for (const item of items) {
  try {
    const i = item.json || {};
    const poml = i.poml;
    if (typeof poml !== 'string' || !poml.trim()) {
      out.push({ json: { error: 'json.poml (string) is required' } });
      continue;
    }

    const componentMap = normalizeComponentSpec(i.componentSpec);
    const attrSpec     = normalizeAttributeSpec(i.attributeSpec);

    const ctx = i.context || {};
    const ast = parsePoml(poml);
    const options = {
      listStyle: i.listStyle || 'dash',
      speakerMode: Boolean(i.speakerMode),
      messages: [],
      componentMap,
      attrSpec,
    };

    if (options.speakerMode) {
      compileNodes(ast, ctx, 'system', options); // messages collected
      if (options.messages.length === 0) {
        const txt = trimLines(compileNodes(ast, ctx, 'user', { ...options, speakerMode: false })).trim();
        if (txt) options.messages.push({ role: 'user', content: txt });
      }
      const prompt = options.messages.map(m => `<<${m.role.toUpperCase()}>>\n${m.content}`).join('\n\n');
      out.push({ json: { prompt, messages: options.messages } });
    } else {
      const prompt = trimLines(compileNodes(ast, ctx, 'system', options)).trim();
      out.push({ json: { prompt } });
    }
  } catch (err) {
    out.push({ json: { error: err?.message || String(err) } });
  }
}

return out;
