const telegramData = $('Webhook Telegram').first().json;
const chatHistory = $('Chat history').all();

const userId = telegramData.message.from.id;
const userName = telegramData.message.from.first_name || telegramData.message.from.username || 'Anonymous';
let userMessage = telegramData.message.text;
const chatId = telegramData.message.chat.id;

const commandMatch = userMessage.match(/^\/(\w+)(?:@\w+)?(?:\s+(.*))?/);
const command = commandMatch ? commandMatch[1].toLowerCase() : '';

const originalMessage = userMessage;
userMessage = userMessage.replace(/@GiggleGPTBot/gi, '').trim();
const isEmptyAfterTag = !userMessage || userMessage.length === 0;

const recentMessages = chatHistory.map(item => {
  const msgText = item.json.message_text || '';
  const author = item.json.message_type === 'bot' ? 'GiggleGPTBot' : 
                 (item.json.first_name || item.json.username || 'Anonymous');
  return `${author}: ${msgText}`;
}).slice(0, 15).join('\n'); 

let contentType = 'mixed';
const lowerMessage = userMessage.toLowerCase();
if (lowerMessage.includes('joke') || lowerMessage.includes('funny')) {
  contentType = 'funny';
} else if (lowerMessage.includes('motivat') || lowerMessage.includes('motivat')) {
  contentType = 'inspiring';
}

if (isEmptyAfterTag) {
  const types = ['funny', 'inspiring', 'mixed'];
  contentType = types[Math.floor(Math.random() * types.length)];
}

const now = new Date();
const hour = now.getHours();
let timeContext = '';
if (hour < 6) timeContext = 'night';
else if (hour < 12) timeContext = 'morning';
else if (hour < 18) timeContext = 'day';
else timeContext = 'evening';

return [{
  json: {
    userId,
    userName,
    userMessage: isEmptyAfterTag ? 'Random phrase' : userMessage,
    originalMessage,
    chatId,
    contentType,
    command,
    timeContext,
    recentMessages,
    chatType: telegramData.message.chat.type,
    isRandomRequest: isEmptyAfterTag
  }
}];

