const messageText = $('Webhook Telegram').first().json.message.text
const commandMatch = messageText.match(/^\/(\w+)(?:@\w+)?(?:\s+(.*))?/);
const commandData = commandMatch ? commandMatch[1].toLowerCase() : '';

const safe = (v, d = '') => (v === null || v === undefined ? d : v);

let userStatsItem;
try {
  userStatsItem = $input.first();
} catch (_) {
  userStatsItem = undefined;
}

let topUsersItems = [];
try {
  const topNode = $('Get top users');
  if (topNode && typeof topNode.all === 'function') {
    const raw = topNode.all() || [];
    topUsersItems = raw
      .map(i => (i && i.json) ? i.json : null)
      .filter(Boolean);
  }
} catch (error) {
  console.log('Top users unavailable:', error.message);
}

const userId = safe($('Webhook Telegram').first().json.message.from.id);
const userName = safe($('Webhook Telegram').first().json.message.chat.first_name, 'User');
const chatId = safe($('Webhook Telegram').first().json.message.chat.id);
const command = safe(commandData, '');

let responseText = '';

const formatDateRu = (iso) => {
  try {
    const d = new Date(iso);
    if (isNaN(d.getTime())) return 'Unknown';
    return d.toLocaleString('en-US', { dateStyle: 'short', timeStyle: 'short' });
  } catch {
    return 'Unknown';
  }
};

const calcActivity = (u) => {
  const msgs = Number(u.messages_count || 0);
  const cmds = Number(u.commands_count || 0);
  return msgs + cmds * 2;
};

switch (command) {
  case 'stats': {
    if (userStatsItem && userStatsItem.json) {
      const s = userStatsItem.json;
      responseText =
        `üìä Statistics ${userName}:\n` +
        `üí¨ Messages: ${Number(s.messages_count || 0)}\n` +
        `‚ö° Commands: ${Number(s.commands_count || 0)}\n` +
        `üìÖ Last activity: ${s.last_activity ? formatDateRu(s.last_activity) : 'Unknown'}`;
    } else {
      responseText =
        `üìä Statistics ${userName}:\n` +
        `There is no data yet. Keep chatting and the statistics will appear! üí¨`;
    }
    break;
  }

  case 'top': {
    if (topUsersItems.length > 0) {
      const sorted = [...topUsersItems]
        .sort((a, b) => calcActivity(b) - calcActivity(a))
        .slice(0, 10);

      const lines = sorted.map((u, idx) => {
        const uname = u.user_name || 'User';
        const msgs = Number(u.messages_count || 0);
        const cmds = Number(u.commands_count || 0);
        const total = calcActivity(u);
        return `${idx + 1}. ${uname}\n   üí¨ ${msgs} messages, ‚ö° ${cmds} commands\n üìä Activity: ${total} points`;
      });

      responseText = 'üèÜ Top Active Users:\n\n' + lines.join('\n\n');
    } else {
      responseText =
        'üèÜ Top Active Users:\n\n' +
        'There are no active users in this chat yet. Be the first! üöÄ';
    }
    break;
  }

  case 'help': {
    responseText =
      'ü§ñ GiggleGPTBot commands:\n\n' +
      'üòÑ /joke ‚Äî funny joke\n' +
      'üí™ /inspire ‚Äî motivation\n' +
      'üé≤ /random ‚Äî random phrase\n' +
      'üî• /roast ‚Äî harsh joke with sarcasm (without insults based on prohibited characteristics)\n' +
      'üìä /stats ‚Äî your statistics\n' +
      'üèÜ /top ‚Äî top users\n' +
      '‚ùì /help ‚Äî this help\n\n' +
      'You can also write @GiggleGPTBot + text ‚Äî I will answer personally.';
    break;
  }

  case 'joke':
  case 'inspire':
  case 'random':
  case 'roast': {
    responseText =
      'This command generates a content response and is processed by another node. ' +
      'Try again - or use /help for a list of available info commands.';
    break;
  }

  default: {
    responseText = 'Unknown command ü§î Use /help for a list of commands.';
  }
}

return [{
  json: {
    userId,
    userName,
    chatId,
    command,
    responseText,
    isInfoCommand: true
  }
}];

